styles.learn.css.content:
/* ============ Layout fixes ballsS ============ */
.learn-wrap {
  display: grid;
  grid-template-columns: 1fr minmax(280px, 360px);
  gap: 20px;
  align-items: start;
}

.hide-mobile { display: block; }

.sections-list { display: flex; flex-direction: column; gap: 20px; }

.learn-rail { background: var(--color-elev); border-radius: var(--radius-md); padding: 16px; }

/* ============ Section card + character ============ */
.section-card {
  --section-card-padding-block: clamp(12px, 2vw, 16px);
  --section-card-padding-inline: clamp(14px, 2.4vw, 18px);

  display: grid;
  grid-template-columns: 1fr auto;
  gap: clamp(12px, 2vw, 18px);
  align-items: flex-start;
  background: #0d1420;
  border: 1px solid #1a2635;
  border-radius: 18px;
  padding: var(--section-card-padding-block) var(--section-card-padding-inline);
  width: min(100%, var(--section-card-max, 100%));
  position: relative;
}

/* Ensure mascot + bubble can overlap the left column safely */

.section-card__left {
  flex: 1 1 auto;
  position: relative;
  z-index: 1;
  min-height: 180px;
  min-width: 0;
}

.section-card__header { margin-bottom: clamp(8px, 1.4vw, 12px); }
.section-title { margin: 0; font-size: var(--fs-500); }
.section-subtitle {
  margin: 6px 0 0;
  color: #8fa7c6;
  font-size: clamp(13px, 2.2vw, 15px);
  line-height: 1.45;
}

.section-card__img {
  position: relative;
  z-index: 3;
  width: clamp(80px, 11vw, 100px);
  flex: 0 0 auto;
  overflow: visible;
}

.character {
  position: relative;
  width: 100%;
  aspect-ratio: 1 / 1;
  display: grid;
  place-items: end center;

  /* Desktop default */
  transform: translateY(75px);
}

/* Override for smaller screens */
@media (max-width: 700px) {
  .character {
    transform: translateY(50px);
  }
}

.character__img {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: auto;
   margin-top: 0;
}

/* ============ Speech bubble ============ */
.speech-bubble {
  position: absolute;
  right: -10%;
  bottom: 100%;
  transform: translateY(-15px);
  display: inline-block;
  width: max-content;
  max-width: min(46ch, 60vw);
  white-space: normal;
  overflow-wrap: break-word;

  background: #1a6460;
  color: #e6f1ff;
  border: 1px solid #3d7b78;
  border-radius: 14px;
  padding: clamp(8px, 1.8vw, 12px) clamp(10px, 2.2vw, 14px);
  box-shadow: 0 6px 14px rgba(0,0,0,.25);
  z-index: 4; /* <- higher than left column */
}

.speech-bubble,
.speech-bubble::after {
  pointer-events: none;
}

/* Bubble should be visible on the Learn list, hidden only on the detail page */
.sections-list .speech-bubble { display: inline-block; }
.section-page .speech-bubble { display: none !important; }

.speech-bubble::after {
  content: "";
  position: absolute;
  top: 100%;
  right: 22px;
  width: 0;
  height: 0;
  border-left: 10px solid transparent;
  border-right: 10px solid transparent;
  border-top: 10px solid #1a6460;
  filter: drop-shadow(0 2px 2px rgba(0,0,0,.25));
}

.speech-line {
  margin: 0;
  line-height: 1.15;
  white-space: normal;
  overflow-wrap: break-word;
}


.speech-line.romanised {
  font-weight: 600;
  font-size: clamp(11px, 2.3vw, 13px);
  color: #9db8ff;
  letter-spacing: 0.3px;
}

.speech-line.sinhala {
  font-weight: 700;
  font-size: clamp(14px, 3vw, 18px);
  color: #e6f1ff;
}

.section-card__actions {
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  gap: clamp(8px, 2vw, 16px);
  margin-top: clamp(12px, 2vw, 16px);
  position: relative;
  z-index: 2;
}

.btn-continue {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border-radius: 999px;
  background: linear-gradient(135deg, #7b5cff, #5aa9ff);
  color: #0b1320;
  font-weight: 700;
  border: none;
}

.btn-continue:hover,
.btn-continue:focus-visible {
  filter: brightness(1.05);
  outline: none;
}

@media (max-width: 700px) {
  :root {
    --mob-gutter: 8px;
    --bubble-clearance: clamp(12px, 4vw, 20px);
  }

  html,
  body {
    overflow-x: hidden;
  }

  .learn-wrap {
    grid-template-columns: 1fr;
    padding-inline: var(--mob-gutter);
    max-width: 100vw;
    box-sizing: border-box;
}

  .hide-mobile {
    display: none !important;
  }

  .sections-list {
    padding-inline: var(--mob-gutter);
    max-width: 100vw;
    box-sizing: border-box;
 }

  .learn-wrap,
  .sections-list {
    margin-inline: auto;
  }

  .section-card {
    --section-card-max: clamp(280px, 96vw, 640px);
    margin-inline: auto;
    max-width: calc(100vw - (var(--mob-gutter) * 2));
    padding-top: calc(var(--section-card-padding-block) + var(--bubble-clearance));
  }
  .section-card__actions {
    justify-content: flex-start;
  }
  .section-card__left {
    min-height: clamp(180px, 52vw, 240px);
  }
  .section-card__img {
    width: clamp(80px, 22vw, 120px);
  }
  .speech-bubble {
    max-width: min(46ch, 94vw);
  }
}

@media (max-width: 480px) {
  .section-card { align-items: stretch; }
  .section-card__img { width: clamp(76px, 26vw, 110px); }
  .speech-bubble { max-width: min(40ch, 94vw); }
}

/* ============ Progress bar / buttons scale ============ */
.progress {
  position: relative;
  height: clamp(12px, 2.2vw, 18px);
  background: #111927;
  border: 1px solid #1e2b3b;
  border-radius: 12px;
  overflow: hidden;
  max-width: 100%;
  margin-inline: 0;
}

.progress-row {
  --trophy-size: clamp(40px, 7vw, 64px);
  display: flex;
  align-items: center;
  gap: clamp(4px, 1vw, 8px);
}

.progress-row .progress {
  flex: 0 1 auto;
  width: max(calc(100% - var(--trophy-size) - 30px), 120px);
}

.progress__fill {
  position: absolute;
  inset: 0 auto 0 0;
  width: 0;
  background: linear-gradient(90deg, #2ee6a6, #1dd4f2);
  transition: width 160ms ease;
}

.progress__nums {
  position: relative;
  z-index: 1;
  display: grid;
  place-items: center;
  height: 100%;
  font-size: clamp(10px, 2vw, 12px);
  color: #a8b3c7;
}
.progress__trophy {
  flex: 0 0 var(--trophy-size);
  width: var(--trophy-size);
  height: var(--trophy-size);
}

.btn-continue {
  padding: clamp(8px, 1.6vw, 10px) clamp(12px, 2.4vw, 16px);
  text-transform: uppercase;
  cursor: pointer;
  font-size: clamp(12px, 2.2vw, 14px);
  min-width: clamp(120px, 28vw, 180px);
  letter-spacing: 0.5px;
}

.btn-continue[disabled] {
  opacity: .5;
  cursor: not-allowed;
}

/* See Details Button */
.see-details {
  display: inline-flex;
  align-items: center;
  gap: 6px;
  padding: 6px 0;
  border: 0;
  background: none;
  cursor: pointer;
  font-weight: 600;
  letter-spacing: 0.5px;
  color: #8fb0ff;
  text-transform: uppercase;
  font-size: clamp(12px, 2vw, 13px);
}
.see-details::after {
  content: '‚ñæ';
  font-size: 0.9em;
  transition: transform 0.3s ease;
}
.see-details[aria-expanded="true"]::after {
  transform: rotate(180deg);
}
.see-details:focus-visible {
  outline: 2px solid #8fb0ff;
  outline-offset: 3px;
}

/* Section Overview Panel */
.section-overview {
  position: relative;
  margin-top: clamp(10px, 2vw, 16px);
  padding: clamp(14px, 2.2vw, 18px);
  border-radius: 12px;
  border: 1px solid rgba(143, 176, 255, 0.2);
  background: rgba(13, 22, 35, 0.9);
  box-shadow: 0 20px 45px rgba(8, 15, 26, 0.55);
  overflow: hidden;
  transition: max-height 0.3s ease;
  max-height: 0;
}
.overview-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: clamp(10px, 1.8vw, 14px);
}
.overview-heading {
  margin: 0;
  font-size: clamp(16px, 2.6vw, 18px);
  color: #e4ecff;
}
.overview-close {
  border: none;
  background: none;
  color: #9ab0d6;
  font-size: clamp(20px, 4vw, 24px);
  cursor: pointer;
  line-height: 1;
  padding: 4px 6px;
  border-radius: 8px;
}
.overview-close:hover,
.overview-close:focus-visible {
  color: #ffffff;
  background: rgba(143, 176, 255, 0.1);
}
.overview-tagline { font-weight: 600; color: #a7c7ff; }
.overview-summary { color: #d3def0; line-height: 1.55; margin: 0 0 16px; }
.overview-subheading { margin: 20px 0 10px; font-size: clamp(14px, 2.3vw, 16px); color: #e6ecff; }
.overview-hints { padding-left: 20px; color: #c1cee2; }
.overview-concepts { display: grid; gap: clamp(14px, 2.4vw, 18px); }
.overview-concept h5 { margin: 0 0 6px; font-size: clamp(13px, 2.2vw, 15px); color: #f2f5ff; }
.overview-concept p { color: #c3d4ea; line-height: 1.5; }
.overview-examples { display: grid; gap: 10px; }
.overview-example { padding: 10px 12px; border-radius: 10px; background: rgba(20, 32, 48, 0.85); border: 1px solid rgba(143, 176, 255, 0.15); }
.example-l1 { font-weight: 600; color: #f1f5ff; }
.example-gloss { color: #99acc9; font-size: 0.95em; }
.overview-cta { margin-top: clamp(16px, 3vw, 22px); }
.overview-placeholder { color: #a2afc6; font-style: italic; }
.overview-cta-button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 6px;
  padding: clamp(8px, 1.8vw, 12px) clamp(14px, 2.6vw, 18px);
  border-radius: 999px;
  border: none;
  background: linear-gradient(135deg, #5d7cff, #6ad1ff);
  color: #0b1320;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.6px;
  cursor: pointer;
  text-decoration: none;
}
.overview-cta-button:hover,
.overview-cta-button:focus-visible {
  filter: brightness(1.05);
}
.overview-cta-button:focus-visible {
  outline: 2px solid rgba(143, 176, 255, 0.7);
  outline-offset: 2px;
}


.locked-note { font-size: 0.75rem; color: var(--color-muted); margin-top: 4px; }

/* Section detail page */
.section-page {
  padding: clamp(16px, 4vw, 28px);
  display: flex;
  flex-direction: column;
  gap: clamp(20px, 4vw, 32px);
}

.section-page__hero {
  display: grid;
  grid-template-columns: minmax(0, 1fr) minmax(160px, 240px);
  gap: clamp(16px, 4vw, 32px);
  align-items: end;
}

.section-page__info h2 {
  margin: 0 0 10px;
  font-size: clamp(1.6rem, 3vw, 2rem);
}

.section-page__description {
  margin: 0 0 16px;
  color: var(--color-muted);
  font-size: clamp(0.95rem, 2.2vw, 1.05rem);
}

.section-page__mascot {
  justify-self: center;
}

.btn-back {
  background: none;
  border: none;
  color: var(--color-accent);
  margin-bottom: 4px;
  cursor: pointer;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  font-weight: 700;
}

.unit-path {
  --unit-gap: clamp(12px, 2.2vw, 18px);
  --bubble-size: clamp(44px, 9vw, 58px);
  position: relative;
  display: flex;
  flex-direction: column;
  gap: var(--unit-gap);
}

.unit-node {
  display: grid;
  grid-template-columns: auto minmax(0, 1fr);
  gap: clamp(12px, 2vw, 16px);
  align-items: flex-start;
}

.unit-node__stem {
  position: relative;
  width: var(--bubble-size);
  min-height: calc(var(--bubble-size) + var(--unit-gap));
  display: flex;
  justify-content: center;
  align-items: center;
}

.unit-node__line {
  position: absolute;
  width: 4px;
  background: rgba(255, 255, 255, 0.08);
  border-radius: 999px;
  left: 50%;
  transform: translateX(-50%);
}

.unit-node__line--top {
  top: calc(-1 * var(--unit-gap));
  height: calc(var(--unit-gap) + var(--bubble-size) / 2);
}

.unit-node__line--bottom {
  bottom: calc(-1 * var(--unit-gap));
  height: calc(var(--unit-gap) + var(--bubble-size) / 2);
}

.unit-node:first-child .unit-node__line--top,
.unit-node:last-child .unit-node__line--bottom {
  display: none;
}

.unit-node__bubble {
  display: grid;
  place-items: center;
  width: var(--bubble-size);
  height: var(--bubble-size);
  background: #203049;
  border: 3px solid #415b8b;
  border-radius: 50%;
  font-size: clamp(20px, 4vw, 28px);
  box-shadow: 0 6px 14px rgba(0,0,0,0.25);
}

.unit-node.is-locked .unit-node__bubble {
  opacity: 0.55;
  border-color: rgba(255, 255, 255, 0.2);
}

.unit-node.is-complete .unit-node__bubble {
  border-color: #2ee6a6;
  background: linear-gradient(135deg, rgba(46, 230, 166, 0.2), rgba(29, 212, 242, 0.25));
}

.unit-node__content {
  background: rgba(13, 20, 32, 0.75);
  border: 1px solid rgba(65, 91, 139, 0.25);
  border-radius: 18px;
  padding: clamp(12px, 2.6vw, 18px);
  width: 100%;
  box-shadow: 0 12px 24px rgba(0,0,0,0.15);
}

.unit-node__header {
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: clamp(10px, 2vw, 16px);
  background: none;
  border: none;
  color: inherit;
  padding: 0;
  text-align: left;
  cursor: pointer;
  text-transform: uppercase;
  letter-spacing: 0.4px;
  font-weight: 700;
}

.unit-node__header[disabled] {
  cursor: not-allowed;
  opacity: 0.6;
}

.unit-node__title-wrap {
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.unit-node__title {
  font-size: clamp(1rem, 2.4vw, 1.1rem);
  text-transform: none;
  letter-spacing: 0;
}

.unit-node__status {
  font-size: clamp(0.7rem, 2vw, 0.8rem);
  color: var(--color-muted);
  text-transform: uppercase;
}

.unit-node__meta {
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  gap: 6px;
  font-size: clamp(0.7rem, 2vw, 0.8rem);
}

.unit-node__count {
  letter-spacing: 0.3px;
}

.unit-node__progress {
  position: relative;
  display: block;
  width: clamp(80px, 18vw, 120px);
  height: 6px;
  border-radius: 999px;
  background: rgba(255,255,255,0.08);
  overflow: hidden;
}

.unit-node__progress-fill {
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  background: linear-gradient(90deg, #2ee6a6, #1dd4f2);
}

.unit-node__lessons {
  margin-top: clamp(12px, 2.4vw, 16px);
  border-top: 1px solid rgba(255,255,255,0.08);
  padding-top: clamp(10px, 2vw, 14px);
}

.unit-node.is-open .unit-node__lessons {
  animation: unitFade 160ms ease-out;
}

@keyframes unitFade {
  from { opacity: 0; transform: translateY(-4px); }
  to { opacity: 1; transform: translateY(0); }
}

.lesson-list {
  list-style: none;
  margin: 0;
  padding: 0;
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.lesson {
  display: grid;
  grid-template-columns: auto minmax(0, 1fr) auto;
  gap: 12px;
  align-items: center;
  background: rgba(8, 14, 24, 0.7);
  border-radius: 12px;
  padding: 8px 12px;
  font-size: clamp(0.8rem, 2vw, 0.9rem);
}

.lesson--locked { opacity: 0.6; }
.lesson--completed { background: rgba(46, 230, 166, 0.12); }

.lesson__icon {
  font-size: 1rem;
}

.lesson__title {
  font-weight: 600;
}

.lesson__state {
  font-size: 0.75rem;
  text-transform: uppercase;
  letter-spacing: 0.3px;
  color: var(--color-muted);
}

.unit-path__empty {
  margin: 0;
  color: var(--color-muted);
}

@media (max-width: 820px) {
  .section-page__hero {
    grid-template-columns: 1fr;
    align-items: center;
  }

  .section-page__mascot {
    order: -1;
  }

  .unit-node {
    grid-template-columns: minmax(44px, 54px) 1fr;
  }
}

@media (max-width: 600px) {
  .section-page {
    padding-inline: var(--mob-gutter, 12px);
  }

  .unit-node__content {
    padding: clamp(10px, 3vw, 14px);
  }

  .lesson {
    grid-template-columns: auto minmax(0, 1fr);
    grid-template-rows: auto auto;
    row-gap: 4px;
  }

  .lesson__state {
    grid-column: 1 / -1;
  }
}

.unit {
  padding: 1rem 0;
  position: relative;
}

.unit-header {
  text-align: center;
  margin: 1rem 0;
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.unit-header h2 {
  margin: 0;
  font-size: clamp(1.1rem, 2.6vw, 1.4rem);
  font-weight: 700;
}

.unit-header hr {
  flex: 1 1 auto;
  border: none;
  border-top: 1px solid rgba(255, 255, 255, 0.15);
}

.unit .unit-path {
  --node-size: clamp(72px, 18vw, 96px); /* bumped up from 60‚Äì78px */
  --sway: clamp(42px, 12vw, 96px);
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: clamp(1.8rem, 5vw, 3rem);
  width: min(100%, 340px);
  margin: 0 auto;
  padding: clamp(1.5rem, 4vw, 2.5rem) 0;
}

.unit .unit-connector {
  position: absolute;
  top: 0;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 4px;
  background: none;
  z-index: -1;
  pointer-events: none;
  overflow: visible;
}

.unit .unit-connector__trail {
  position: absolute;
  top: 0;
  left: 50%;
  transform: translateX(-50%);
  height: 100%;
  width: clamp(52px, 12vw, 76px);
  display: block;
}

.unit .unit-connector__fill {
  width: 100%;
}

.unit .lesson-row {
  --lesson-offset: 0px;

  position: relative;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
}

.unit .lesson-row > * {
  position: relative;
  z-index: 1;
}

.unit .lesson-bubble {
  position: absolute;
  top: calc(100% + 12px);
  left: 50%;
  --bubble-nudge: -12px; /* adjust as needed */
  transform: translate(calc(-50% + var(--lesson-offset) - 10px), -6px) scale(0.88);
  transform-origin: top center;
  width: min(260px, 82vw);
  pointer-events: none;
  opacity: 0;
  transition: opacity 200ms ease, transform 220ms cubic-bezier(0.16, 1, 0.3, 1);
  z-index: 5;
}

.unit .lesson-bubble.is-open {
  opacity: 1;
  transform: translate(calc(-50% + var(--lesson-offset) + var(--bubble-nudge)), 0) scale(1);
}

.unit .lesson-bubble[hidden] {
  display: none;
}

.lesson-bubble__content {
  background: rgba(8, 19, 33, 0.96);
  border-radius: 16px;
  border: 1px solid rgba(255, 255, 255, 0.12);
  padding: 16px;
  box-shadow: 0 12px 32px rgba(0, 0, 0, 0.35);
  color: #f1f6ff;
  font-size: 0.875rem;
  pointer-events: auto;
  position: relative;
}

.lesson-bubble__content::before {
  content: "";
  position: absolute;
  top: 0;
  left: 50%;
  transform: translate(-50%, -100%);
  width: 0;
  height: 0;
  border-left: 14px solid transparent;
  border-right: 14px solid transparent;
  border-bottom: 14px solid rgba(8, 19, 33, 0.96);
  filter: drop-shadow(0 6px 6px rgba(0, 0, 0, 0.35));
}

.lesson-bubble__header {
  display: flex;
  flex-direction: column;
  gap: 4px;
  align-items: flex-start;
}

.lesson-bubble__title {
  margin: 0;
  font-weight: 700;
  font-size: clamp(0.9rem, 2.4vw, 1.05rem);
}

.lesson-bubble__meta {
  margin: 0;
  font-size: clamp(0.75rem, 2vw, 0.85rem);
  color: rgba(214, 231, 255, 0.78);
  letter-spacing: 0.2px;
}

.lesson-bubble__footer {
  margin-top: 14px;
}

.lesson-bubble__cta {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  background: linear-gradient(90deg, #2ee6a6, #1dd4f2);
  color: #04121f;
  font-weight: 700;
  font-size: clamp(0.75rem, 2vw, 0.85rem);
  border: none;
  border-radius: 999px;
  padding: 10px 16px;
  cursor: pointer;
  transition: transform 120ms ease, box-shadow 120ms ease;
}

.lesson-bubble__cta[data-lesson-status="locked"] {
  background: rgba(255, 255, 255, 0.08);
  color: rgba(241, 246, 255, 0.72);
  cursor: not-allowed;
}

.lesson-bubble__cta:not([data-lesson-status="locked"]):hover,
.lesson-bubble__cta:not([data-lesson-status="locked"]):focus-visible {
  transform: translateY(-1px);
  box-shadow: 0 6px 16px rgba(29, 212, 242, 0.35);
}

.lesson-bubble__cta:focus-visible {
  outline: 2px solid rgba(241, 246, 255, 0.85);
  outline-offset: 2px;
}

.unit .lesson-row--left {
  --lesson-offset: calc(-1 * var(--sway));
}

.unit .lesson-row--right {
  --lesson-offset: var(--sway);
}

.unit .lesson-row > :not(.lesson-bubble) {
  transform: translateX(var(--lesson-offset));
}

.unit .lesson-row--center {
  --lesson-offset: 0px;
}

.unit .lesson {
  background: none;
  border: none;
  cursor: pointer;
  width: var(--node-size);
  height: var(--node-size);
  padding: 0;
}

.unit .lesson img {
  width: 100%;
  height: auto;
  display: block;
}

.unit .lesson--locked img {
  opacity: 0.55;
}

.unit .lesson--completed img,
.unit .lesson--complete img {
  filter: drop-shadow(0 0 6px rgba(255, 215, 0, 0.8));
}

.unit .lesson--unlocked img,
.unit .lesson--start img {
  filter: drop-shadow(0 0 4px rgba(143, 176, 255, 0.6));
}

.unit .lesson-row.mascot img {
  width: calc(var(--node-size) * 1.15);
  height: auto;
}

.unit .lesson-row.trophy .lesson,
.unit .lesson-row.chest .lesson {
  position: relative;
}

.unit .lesson-row.trophy .lesson img,
.unit .lesson-row.chest .lesson img {
  position: absolute;
  top: 50%;
  left: 50%;
  margin-left: 30px;
  transform: translate(-50%, -50%) scale(1.3); /* make 30% bigger */
  width: 100%;
  height: auto;
}
.unit .lesson-row.trophy .lesson-bubble,
.unit .lesson-row.chest .lesson-bubble {
  top: calc(100% + 28px); /* was 12px, now 28px lower */
  transform: translateX(-100px);
}

/* ============ Section Units Panel ============ */
.section-units {
  grid-column: 1 / -1;
  overflow: hidden;
  transition: max-height 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  max-height: 0;
  margin-top: 0;
}

.section-units.is-open {
  margin-top: 20px;
}

.section-units[hidden] {
  display: block;
  max-height: 0;
}

/* Highlight animation for focused section */
@keyframes section-highlight {
  0%, 100% {
    box-shadow: 0 0 0 0 rgba(59, 130, 246, 0);
  }
  50% {
    box-shadow: 0 0 0 4px rgba(59, 130, 246, 0.4);
  }
}

.section-card.section-highlight {
  animation: section-highlight 1s ease-out;
}

@media (max-width: 700px) {
}

