styles.css.content:
/* === Fonts =================================================== */
@font-face {
  font-family: "Dolpino";
  src: url("assets/font/Dolpino.ttf") format("truetype");
  font-weight: 400;
  font-style: normal;
  font-display: swap;
}

/* Font stacks (with reliable fallbacks) */
:root {
  --font-heading: "Dolpino", "Feather", "DIN Next Rounded", ui-rounded, ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, "Helvetica Neue", Arial, "Noto Sans", "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", sans-serif;
  --font-body: "Dolpino", "Feather", "DIN Next Rounded", ui-rounded, ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, "Helvetica Neue", Arial, "Noto Sans", "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", sans-serif;

  /* Optional optical adjustments */
  --heading-letterspacing: 0.5px;
  --body-letterspacing: 0.3px;
  --heading-weight: 700;
  --body-weight: 400;
  --ui-weight: 500;
}
/* ==========================================================================
   App Stylesheet
   - Mobile-first, fully responsive
   - Light/Dark theme via [data-theme] or prefers-color-scheme
   - Utility-first helpers for fast rearranging on mobile
   ========================================================================== */

/* -----------------------------
   1) CSS Reset + Base
------------------------------ */
*, *::before, *::after { box-sizing: border-box; }
* { min-width: 0; }
html, body { height: 100%; }
body, h1, h2, h3, h4, h5, h6, p, figure, blockquote, dl, dd { margin: 0; }
ul[role="list"], ol[role="list"] { list-style: none; margin: 0; padding: 0; }
img, picture, video, canvas, svg { display: block; max-width: 100%; }
input, button, textarea, select { font: inherit; color: inherit; }
button { cursor: pointer; }
a { color: inherit; text-decoration: none; }
:focus { outline: none; }
:focus-visible { outline: 2px solid var(--color-accent); outline-offset: 2px; }
[hidden] { display: none !important; }

/* -----------------------------
   2) Theme Tokens
------------------------------ */
:root {
  /* Colors */
  --color-bg: #0b1320;
  --color-surface: #101a2b;
  --color-elev: #121f36;
  --color-text: #eaf1f7;
  --color-muted: #a9b6c6;
  --color-border: #22324a;

  --color-accent: #3ec0ff;
  --color-accent-600: #1aa8ef;
  --color-accent-700: #0e87c1;
  --color-positive: #68d391;
  --color-warning: #fbbf24;
  --color-danger: #f87171;

  /* Typography */
  --font-sans: ui-sans-serif, system-ui, -apple-system, "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
  --fs-300: clamp(0.88rem, 0.85rem + 0.2vw, 0.98rem);
  --fs-400: clamp(1rem, 0.95rem + 0.4vw, 1.125rem);
  --fs-500: clamp(1.125rem, 1rem + 0.8vw, 1.375rem);
  --fs-600: clamp(1.35rem, 1.1rem + 1.2vw, 1.75rem);
  --fs-700: clamp(1.7rem, 1.3rem + 2vw, 2.25rem);
  --fs-800: clamp(2rem, 1.5rem + 3vw, 3rem);

  /* Spacing scale */
  --space-2xs: .25rem;
  --space-xs: .5rem;
  --space-sm: .75rem;
  --space-md: 1rem;
  --space-lg: 1.5rem;
  --space-xl: 2rem;
  --space-2xl: 3rem;

  /* Radius & Shadow */
  --radius-sm: .5rem;
  --radius-md: 1rem;
  --radius-lg: 1.5rem;
  --shadow-1: 0 1px 2px rgba(0,0,0,.2), 0 8px 24px rgba(0,0,0,.25);
  --shadow-2: 0 2px 4px rgba(0,0,0,.25), 0 12px 32px rgba(0,0,0,.3);


  /* Container widths */
  --container: 1200px;
}

/* Light theme opt-in */
[data-theme="light"] {
  --color-bg: #f5f8fc;
  --color-surface: #ffffff;
  --color-elev: #f7fafc;
  --color-text: #0f172a;
  --color-muted: #475569;
  --color-border: #e2e8f0;

  --color-accent: #0ea5e9;
  --color-accent-600: #0284c7;
  --color-accent-700: #0369a1;
}

/* Auto dark if user prefers */
@media (prefers-color-scheme: light) {
  :root:not([data-theme="dark"]) { /* keep default unless overridden */ }
}

/* Base */
html, body {
  font-family: var(--font-body);
  font-weight: var(--body-weight);
  text-rendering: optimizeLegibility;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body {
  font-size: var(--fs-400);
  line-height: 1.5;
  letter-spacing: var(--body-letterspacing);
  background: radial-gradient(1200px 800px at 10% -10%, rgba(62,192,255,.08), transparent 60%),
              radial-gradient(800px 600px at 90% 10%, rgba(14,135,193,.08), transparent 60%),
              var(--color-bg);
  color: var(--color-text);
}

/* Headings use Feather Bold */
h1, h2, h3, h4, .hero__title, .section-title, .sidebar__title, .card__title, .media__title {
  font-family: var(--font-heading);
  font-weight: var(--heading-weight);
  letter-spacing: var(--heading-letterspacing);
}

/* UI elements keep DIN Next Rounded medium for better contrast */
.btn, .chip, .tabbar__btn, .hud__val, .badge, nav a, .sidebar__link, .sidebar__nav button {
  font-family: var(--font-body);
  font-weight: var(--ui-weight);
}

.tabbar__btn,
.sidebar__nav button {
  letter-spacing: 0.5px;
}

/* -----------------------------
   3) Layout Utilities
------------------------------ */
.container {
  width: min(100% - 2rem, var(--container));
  margin-inline: auto;
}

.stack > * + * { margin-top: var(--stack-space, var(--space-md)); }
.cluster {
  display: flex; flex-wrap: wrap; gap: var(--cluster-space, var(--space-md));
  align-items: center;
}
.switcher {
  display: flex; flex-wrap: wrap; gap: var(--space-md);
}
.switcher > * { flex-grow: 1; flex-basis: calc((var(--switcher-threshold, 28rem) - 100%) * 999); }
.center {
  box-sizing: content-box;
  max-inline-size: var(--measure, 60ch);
  margin-inline: auto;
  padding-inline: var(--space-md);
}
.grid {
  display: grid; gap: var(--grid-gap, var(--space-lg));
  grid-template-columns: repeat(12, 1fr);
}
.grid.is-2 { grid-template-columns: repeat(2, 1fr); }
.grid.is-3 { grid-template-columns: repeat(3, 1fr); }
.grid.is-4 { grid-template-columns: repeat(4, 1fr); }

/* Responsive collapse for mobile */
@media (max-width: 900px) {
  .grid { grid-template-columns: repeat(6, 1fr); }
}
@media (max-width: 600px) {
  .grid, .grid.is-2, .grid.is-3, .grid.is-4 { grid-template-columns: 1fr; }
}

/* Order utilities for quick rearranging */
.order-1 { order: 1; } .order-2 { order: 2; } .order-3 { order: 3; } .order-4 { order: 4; }
@media (min-width: 900px) {
  .lg\\:order-1 { order: 1; } .lg\\:order-2 { order: 2; } .lg\\:order-3 { order: 3; } .lg\\:order-4 { order: 4; }
}

/* Responsive show/hide */
.show-mobile { display: block; }
.hide-mobile { display: none; }
@media (min-width: 900px) {
  .show-mobile { display: none; }
  .hide-mobile { display: block; }
}

/* Width helpers */
.w-100 { width: 100%; }
.max-w-sm { max-width: 28rem; } .max-w-md { max-width: 36rem; } .max-w-lg { max-width: 48rem; }

/* -----------------------------
   4) Header / Nav
------------------------------ */
.header {
  position: sticky; top: 0; z-index: 50;
  background: color-mix(in oklab, var(--color-surface) 90%, transparent);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid var(--color-border);
}
.header__inner {
  display: flex; align-items: center; justify-content: space-between;
  gap: var(--space-md); padding: .75rem 0;
}
.logo { display: inline-flex; align-items: center; gap: .5rem; font-weight: 700; font-size: var(--fs-500); }
.logo img { width: 32px; height: 32px; }

.nav { display: flex; align-items: center; gap: var(--space-lg); }
.nav a { color: var(--color-muted); font-weight: var(--ui-weight); }
.nav a:hover, .nav a[aria-current="page"] { color: var(--color-text); }

.nav__toggle {
  display: inline-flex; align-items: center; justify-content: center;
  width: 40px; height: 40px; border-radius: var(--radius-sm);
  border: 1px solid var(--color-border);
  background: var(--color-elev); box-shadow: var(--shadow-1);
}
@media (min-width: 900px) {
.nav__toggle { display: none; }
}
.nav--mobile {
  display: none;
  position: fixed; inset: 58px 0 0 0; /* under sticky header */
  background: var(--color-surface);
  border-top: 1px solid var(--color-border);
  padding: var(--space-lg);
}
.nav--mobile.open { display: block; }
.nav--mobile a {
  display: block; padding: .875rem 0; border-bottom: 1px dashed var(--color-border);
}

/* -----------------------------
   5) Hero / Sections / Footer
------------------------------ */
.section { padding-block: var(--space-2xl); }
.section--tight { padding-block: var(--space-xl); }

.hero {
  padding-block: var(--space-2xl);
  display: grid; gap: var(--space-xl);
  grid-template-columns: 1fr;
}
.hero__title { font-size: var(--fs-800); line-height: 1.1; letter-spacing: var(--heading-letterspacing); }
.hero__subtitle { color: var(--color-muted); font-size: var(--fs-500); margin-top: var(--space-sm); }

@media (min-width: 900px) {
  .hero { grid-template-columns: 6fr 6fr; align-items: center; }
}

.footer {
  border-top: 1px solid var(--color-border);
  background: linear-gradient(180deg, var(--color-surface), var(--color-elev));
  padding-block: var(--space-xl);
  color: var(--color-muted);
}

/* -----------------------------
   6) Cards / Surfaces
------------------------------ */
.card {
background: var(--color-surface);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-lg);
  box-shadow: var(--shadow-1);
  padding: var(--space-lg);
}
.card--elev { background: var(--color-elev); box-shadow: var(--shadow-2); }
.card__title { font-size: var(--fs-600); margin-bottom: .5rem; }
.card__meta { color: var(--color-muted); font-size: var(--fs-300); }

/* -----------------------------
   7) Buttons
------------------------------ */
.btn {
  --btn-bg: var(--color-accent);
  --btn-bg-hover: var(--color-accent-600);
  --btn-bg-active: var(--color-accent-700);
  --btn-text: #081018;

  display: inline-flex; align-items: center; justify-content: center; gap: .5rem;
  padding: .7rem 1rem;
  border-radius: 999px;
  border: 1px solid color-mix(in oklab, var(--btn-bg) 70%, black 10%);
  background: linear-gradient(180deg, color-mix(in oklab, var(--btn-bg) 92%, white 8%), var(--btn-bg));
  color: var(--btn-text);
  font-weight: var(--ui-weight);
  letter-spacing: 0.5px;
  text-shadow: 0 1px 0 rgba(255,255,255,.3);
  box-shadow: 0 6px 16px rgba(0,0,0,.25), inset 0 1px 0 rgba(255,255,255,.2);
  transition: transform .1s ease, filter .2s ease, background .2s ease;
}
.btn:hover { filter: brightness(1.02); transform: translateY(-1px); background: linear-gradient(180deg, color-mix(in oklab, var(--btn-bg-hover) 92%, white 8%), var(--btn-bg-hover)); }
.btn:active { transform: translateY(0); background: var(--btn-bg-active); }
.btn:disabled { opacity: .6; cursor: not-allowed; }

.btn--ghost {
  --btn-bg: transparent; --btn-text: var(--color-text);
  border-color: var(--color-border);
  background: linear-gradient(180deg, color-mix(in oklab, var(--color-surface) 85%, white 15%), var(--color-surface));
  text-shadow: none;
}
.btn--danger { --btn-bg: var(--color-danger); }
.btn--warn { --btn-bg: var(--color-warning); }
.btn--ok { --btn-bg: var(--color-positive); }

/* Icon-only button size helper */
.btn--icon { width: 42px; height: 42px; padding: 0; border-radius: 50%; }

/* -----------------------------
   8) Forms
------------------------------ */
.field { display: grid; gap: .5rem; }
.label { font-weight: 700; font-size: .95em; }
.input, .select, .textarea {
  width: 100%;
  border-radius: var(--radius-sm);
  border: 1px solid var(--color-border);
  background: var(--color-elev);
  padding: .75rem .9rem;
  color: var(--color-text);
  box-shadow: inset 0 1px 0 rgba(255,255,255,.04);
}
.input:focus, .select:focus, .textarea:focus {
  border-color: color-mix(in oklab, var(--color-accent), white 20%);
  box-shadow: 0 0 0 4px color-mix(in oklab, var(--color-accent) 25%, transparent);
}
.help { color: var(--color-muted); font-size: .9em; }

/* -----------------------------
   Profile view
------------------------------ */
.profile-content {
  display: grid;
  gap: var(--space-lg);
  max-width: min(760px, 100%);
  margin-inline: auto;
  align-content: start;
}

.profile-grid {
  display: grid;
  gap: var(--space-lg);
}

@media (min-width: 700px) {
  .profile-grid {
    grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
  }
}

.profile-card {
  display: grid;
  gap: var(--space-md);
}

.profile-login fieldset {
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  padding: var(--space-md);
  display: grid;
  gap: var(--space-md);
  background: color-mix(in oklab, var(--color-surface) 92%, black 8%);
}

.profile-login legend {
  font-weight: 700;
  padding: 0 .5rem;
}

.profile-form__row {
  display: flex;
  gap: var(--space-sm);
  align-items: center;
}

.profile-form__row .input {
  flex: 1;
  min-width: 0;
}

.profile-hint {
  margin: 0;
  font-size: .9em;
  color: var(--color-muted);
}

.profile-welcome {
  margin: 0;
  font-size: var(--fs-500);
  font-weight: 600;
}

.profile-summary .btn {
  justify-self: start;
}

.debug-panel {
  display: grid;
  gap: var(--space-md);
}

.debug-panel__header h2 {
  margin: 0;
  font-size: var(--fs-500);
}

.debug-panel__picker {
  display: grid;
  gap: var(--space-xs);
}

.debug-panel__picker label {
  font-weight: 600;
  font-size: .95em;
}

.debug-panel__controls {
  display: grid;
  gap: var(--space-sm);
}

.debug-control__btn {
  width: 100%;
}

.debug-panel__simulator {
  display: grid;
  gap: var(--space-sm);
  padding: var(--space-md);
  background: color-mix(in oklab, var(--color-elev) 85%, transparent);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
}

.debug-panel__simulator-header {
  display: grid;
  gap: 0.25rem;
}

.debug-panel__simulator-header h3 {
  margin: 0;
  font-size: 1.1rem;
}

.debug-panel__simulator-hint {
  margin: 0;
  font-size: 0.9em;
  color: var(--color-muted);
}

.debug-panel__field {
  display: grid;
  gap: var(--space-xs);
  font-size: 0.95em;
}

.debug-panel__field > span {
  font-weight: 600;
}

.debug-multiselect {
  display: grid;
  gap: var(--space-xs);
  position: relative;
}

.debug-multiselect__toggle {
  justify-self: start;
}

.debug-multiselect__menu {
  position: absolute;
  top: calc(100% + 0.35rem);
  left: 0;
  width: max(220px, 100%);
  background: var(--color-surface);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-sm);
  padding: var(--space-sm);
  box-shadow: var(--shadow-1);
  display: grid;
  gap: var(--space-xs);
  z-index: 30;
}

.debug-multiselect__option {
  display: flex;
  align-items: center;
  gap: var(--space-xs);
  font-size: 0.95em;
}

.debug-multiselect__option input {
  accent-color: var(--color-accent);
}

.debug-multiselect__summary {
  margin: 0;
  font-size: 0.9em;
  color: var(--color-muted);
}

/* Inline form layout that auto-stacks on small screens */
.form-inline {
  display: grid; gap: var(--space-md);
  grid-template-columns: 1fr auto;
}
@media (max-width: 520px) { .form-inline { grid-template-columns: 1fr; } }

/* -----------------------------
   9) Tables (responsive)
------------------------------ */
.table {
  width: 100%; border-collapse: collapse; border: 1px solid var(--color-border);
  border-radius: var(--radius-md); overflow: clip; background: var(--color-surface);
}
.table th, .table td { padding: .9rem; border-bottom: 1px solid var(--color-border); }
.table thead th { background: color-mix(in oklab, var(--color-elev) 70%, transparent); text-align: left; font-size: .95em; }
.table tr:hover td { background: color-mix(in oklab, var(--color-accent) 10%, transparent); }

/* Stacked rows on mobile */
@media (max-width: 600px) {
  .table thead { display: none; }
  .table, .table tbody, .table tr, .table td { display: block; width: 100%; }
  .table tr { border-bottom: 1px solid var(--color-border); }
  .table td { display: grid; grid-template-columns: 8ch auto; gap: .75rem; }
  .table td::before { content: attr(data-label); color: var(--color-muted); font-weight: 700; }
}

/* -----------------------------
   10) Modal / Drawer (mobile friendly)
------------------------------ */
.modal {
  position: fixed; inset: 0; display: none; place-items: center;
  background: rgba(0,0,0,.5); z-index: 100;
}
.modal.open { display: grid; }
.modal__dialog {
  width: min(96vw, 560px);
  background: var(--color-surface); border: 1px solid var(--color-border);
  border-radius: var(--radius-lg); box-shadow: var(--shadow-2);
  padding: var(--space-lg);
}

/* -----------------------------
   11) Badges / Pills
------------------------------ */
.badge {
  display: inline-block; padding: .25rem .6rem; border-radius: 999px;
  font-size: .8em; font-weight: 700;
  background: color-mix(in oklab, var(--color-accent) 25%, transparent);
  border: 1px solid color-mix(in oklab, var(--color-accent), black 10%);
  color: color-mix(in oklab, var(--color-text), white 10%);
}
.badge--ok { background: color-mix(in oklab, var(--color-positive) 25%, transparent); border-color: color-mix(in oklab, var(--color-positive), black 10%); }
.badge--warn { background: color-mix(in oklab, var(--color-warning) 25%, transparent); border-color: color-mix(in oklab, var(--color-warning), black 10%); }
.badge--danger { background: color-mix(in oklab, var(--color-danger) 25%, transparent); border-color: color-mix(in oklab, var(--color-danger), black 10%); }

/* Lesson simulator overlay */
body.lesson-simulator-open {
  overflow: hidden;
}

.lesson-simulator-overlay {
  position: fixed;
  inset: 0;
  z-index: 400;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: clamp(1rem, 3vw, 3rem);
  background: rgba(6, 12, 20, 0.82);
  backdrop-filter: blur(4px);
}

.lesson-simulator-overlay[hidden] {
  display: none;
}

.lesson-simulator-overlay__scrim {
  position: absolute;
  inset: 0;
}

.lesson-simulator {
  position: relative;
  width: min(960px, 100%);
  max-height: min(90vh, 100%);
  overflow: hidden;
  border-radius: var(--radius-lg);
  border: 1px solid var(--color-border);
  background: var(--color-surface);
  box-shadow: var(--shadow-2);
  padding: clamp(1.5rem, 2vw, 2.5rem);
}

.lesson-simulator__close {
  position: absolute;
  top: 0.85rem;
  right: 0.85rem;
  border: none;
  background: transparent;
  color: var(--color-muted);
  font-size: 1.6rem;
  line-height: 1;
}

.lesson-simulator__close:hover,
.lesson-simulator__close:focus-visible {
  color: var(--color-text);
}

.lesson-simulator__content {
  max-height: calc(90vh - 3rem);
  overflow-y: auto;
  padding-top: 1.5rem;
  display: grid;
  gap: var(--space-lg);
}

.lesson-simulator__header {
  display: grid;
  gap: var(--space-xs);
}

.lesson-simulator__eyebrow {
  margin: 0;
  font-size: 0.9em;
  color: var(--color-accent);
  text-transform: uppercase;
  letter-spacing: 0.08em;
}

.lesson-simulator__meta {
  margin: 0;
  color: var(--color-muted);
  font-size: 0.95em;
}

.lesson-simulator__title {
  margin: 0;
  font-size: var(--fs-500);
}

.lesson-simulator__progress {
  margin: 0;
  font-weight: 600;
}

.lesson-simulator__stage {
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  background: color-mix(in oklab, var(--color-elev) 75%, transparent);
  padding: var(--space-md);
  min-height: 18rem;
  overflow-y: auto;
}

.lesson-simulator__exercise-host {
  min-height: 12rem;
}

.lesson-simulator__status {
  margin: 0;
  color: var(--color-muted);
}

.lesson-simulator__controls {
  display: flex;
  justify-content: flex-end;
  gap: var(--space-sm);
}

.lesson-simulator__loader {
  text-align: center;
  padding: var(--space-lg) var(--space-md);
  color: var(--color-muted);
}

.lesson-simulator__error {
  margin: 0;
  padding: var(--space-lg) var(--space-md);
  text-align: center;
  color: var(--color-danger);
  background: color-mix(in oklab, var(--color-danger) 18%, transparent);
  border-radius: var(--radius-md);
}

.lesson-simulator__complete {
  padding: var(--space-lg) var(--space-md);
  text-align: center;
  font-size: var(--fs-500);
  font-weight: 600;
}

.lesson-simulator__next {
  min-width: 11rem;
}

@media (max-width: 720px) {
  .lesson-simulator {
    padding: 1.25rem;
  }

  .lesson-simulator__close {
    top: 0.65rem;
    right: 0.65rem;
  }

  .lesson-simulator__content {
    max-height: calc(90vh - 2.5rem);
  }
}

/* -----------------------------
   12) Progress / Bars
------------------------------ */
.progress {
  width: 100%; height: .75rem; border-radius: 999px;
  background: color-mix(in oklab, var(--color-elev) 80%, transparent);
  border: 1px solid var(--color-border);
  overflow: hidden;
}
.progress__bar {
  height: 100%;
  background: linear-gradient(90deg, var(--color-accent), color-mix(in oklab, var(--color-accent) 80%, white));
}

/* -----------------------------
   13) Media blocks
------------------------------ */
.media {
  display: grid; grid-template-columns: 56px 1fr; gap: var(--space-md); align-items: start;
}
.media__img { width: 56px; height: 56px; border-radius: 12px; overflow: hidden; }
.media__meta { font-size: .9em; color: var(--color-muted); }

/* -----------------------------
   14) Helper Utilities
------------------------------ */
.sr-only {
  position: absolute; width: 1px; height: 1px; padding: 0; margin: -1px;
  overflow: hidden; clip: rect(0, 0, 0, 0); white-space: nowrap; border: 0;
}
.mute { color: var(--color-muted); }
.mono { font-family: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace; }
.rounded-sm { border-radius: var(--radius-sm); }
.rounded-md { border-radius: var(--radius-md); }
.rounded-lg { border-radius: var(--radius-lg); }
.border { border: 1px solid var(--color-border); }
.shadow-1 { box-shadow: var(--shadow-1); }
.shadow-2 { box-shadow: var(--shadow-2); }
.pad-sm { padding: var(--space-sm); } .pad-md { padding: var(--space-md); } .pad-lg { padding: var(--space-lg); }
.gap-sm { gap: var(--space-sm); } .gap-md { gap: var(--space-md); } .gap-lg { gap: var(--space-lg); }
.text-center { text-align: center; }
.text-right { text-align: right; }
.mt-sm { margin-top: var(--space-sm); } .mt-md { margin-top: var(--space-md); } .mt-lg { margin-top: var(--space-lg); }
.mb-sm { margin-bottom: var(--space-sm); } .mb-md { margin-bottom: var(--space-md); } .mb-lg { margin-bottom: var(--space-lg); }

/* -----------------------------
   15) Animations & Motion Prefs
------------------------------ */
@media (prefers-reduced-motion: reduce) {
  * { animation: none !important; transition: none !important; scroll-behavior: auto !important; }
}
.fade-in { animation: fade-in .25s ease both; }
@keyframes fade-in { from { opacity: 0; transform: translateY(6px);} to { opacity: 1; transform: translateY(0);} }

/* -----------------------------
   16) App-specific anchors (optional)
   - Map these classes to your HTML to get instant layouts
------------------------------ */
.app-shell {
  min-height: 100dvh; display: grid; grid-template-rows: auto 1fr auto;
}
.main {
  padding-block: var(--space-xl);
}

/* Example responsive card grid */
.cards {
  display: grid; gap: var(--space-lg);
  grid-template-columns: repeat(1, minmax(0, 1fr));
}
@media (max-width: 599px) {
  .main { padding-inline: var(--space-sm); }   /* less side padding */
  .cards { gap: var(--space-lg); }             /* keep spacing reasonable */
  .card  { margin-inline: var(--space-xs); }   /* less card margin */
}
@media (min-width: 560px) { .cards { grid-template-columns: repeat(2, 1fr); } }
@media (min-width: 960px) { .cards { grid-template-columns: repeat(3, 1fr); } }

/* Example mobile-first two-column section that reorders on desktop */
.split {
  display: grid; gap: var(--space-xl);
  grid-template-columns: 1fr;
}
@media (min-width: 900px) {
  .split { grid-template-columns: 5fr 7fr; align-items: center; }
  .split .split__media { order: 2; }
  .split .split__content { order: 1; }
}

/* Sticky CTA on mobile */
.sticky-cta {
  position: sticky; bottom: 0; z-index: 40;
  background: color-mix(in oklab, var(--color-surface) 90%, transparent);
  backdrop-filter: blur(6px);
  border-top: 1px solid var(--color-border);
  padding: var(--space-md);
}

/* Toast */
.toast {
  position: fixed; inset: auto 1rem 1rem auto; z-index: 100;
  background: var(--color-elev); border: 1px solid var(--color-border);
  border-radius: var(--radius-md); padding: .75rem 1rem; box-shadow: var(--shadow-1);
}

/* Skeleton loaders */
.skeleton {
  border-radius: .5rem;
  background: linear-gradient(90deg, rgba(255,255,255,.04), rgba(255,255,255,.08), rgba(255,255,255,.04));
  animation: shimmer 1.6s infinite linear;
}
@keyframes shimmer {
  0% { background-position: -200% 0; }
  100% { background-position: 200% 0; }
}
/* ===== Layout hotfix (paste at end) ===== */

/* sensible defaults */
*, *::before, *::after { box-sizing: border-box; }
html, body { height: 100%; }
body { margin: 0; overflow-x: hidden; }
img, svg, video { max-width: 100%; height: auto; display: block; }

/* page clamp */
:root {
  --page-max: 1200px;
  --gutter: 16px;
  --sidebar-w: 280px;
  --topbar-h: 64px;
}
.topbar {
  position: sticky;
  top: 0;
  z-index: 50;
  min-height: var(--topbar-h);
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
  padding: 8px var(--gutter);
  backdrop-filter: saturate(1.1) blur(4px);
}

/* desktop grid */
.layout {
  max-width: var(--page-max);
  margin-inline: auto;
  padding: 0 var(--gutter) 80px;
  display: grid;
  grid-template-columns: minmax(0, 1fr); /* mobile default, overridden at ‚â•1024px */
  gap: 20px;
}

/* sticky sidebar on desktop */
.sidebar {
  background: var(--surface-2, transparent);
  border-radius: 10px;
  padding: 12px;
}
@media (min-width: 1024px) {
  .layout {
    grid-template-columns: var(--sidebar-w) minmax(0, 1fr);
    align-items: start;
  }
  .sidebar {
    position: sticky;
    top: calc(var(--topbar-h) + 12px);
    min-height: calc(100dvh - var(--topbar-h) - 24px);
    height: 100%;
    overflow: auto;
    align-self: stretch;
  }
}

/* main section spacing */
.main .view { display: block; }
.section-title { margin: 24px 0 12px; }

/* hero tidy */
.hero {
  display: grid;
  grid-template-columns: 1fr auto;
  gap: 16px;
  align-items: center;
  margin: 10px 0 20px;
}
.hero__title { margin: 0 0 4px; }
.hero__cta { display: flex; gap: 8px; }

/* path grids */
.path-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(220px, 1fr));
  gap: 14px;
}
.path-grid--compact { grid-template-columns: repeat(auto-fill, minmax(180px, 1fr)); }

/* cards */
.card, .course-card {
 border-radius: 12px;
  padding: 14px;
}
.course-card .media { border-radius: 10px; overflow: hidden; }
.course-card .content { display: grid; gap: 8px; }
.course-card .btn { text-transform: uppercase; }

/* tab bar: only on small screens */
.tabbar { position: fixed; inset-block-end: 0; inset-inline: 0; z-index: 60; }
@media (min-width: 768px) { .tabbar { display: none !important; } }

/* overlay layering */
#detailsPanel, .toasts { z-index: 70; }
#drawerScrim, #detailsScrim { z-index: 65; }

/* tighten big empty bands on ultra-wide monitors */
@media (min-width: 1440px) {
  .hero { grid-template-columns: 1fr auto; }
  .card, .course-card { padding: 16px; }
}

/* small-screen single column */
@media (max-width: 767.98px) {
  .hero { grid-template-columns: 1fr; }
  .hero__cta { justify-self: start; }
}

