.match-pairs {
  --exercise-bg: #15354f;
  --exercise-surface: #0c2334;
  --exercise-text: #f5fffa;
  --exercise-muted: rgba(245, 255, 250, 0.7);
  --exercise-accent: #0bcb88;
  --exercise-accent-soft: rgba(11, 203, 136, 0.2);
  --exercise-accent-strong: rgba(11, 203, 136, 0.95);
  --exercise-success: rgba(147, 255, 210, 0.95);
  --exercise-danger: #ff7a7a;
  width: 100%;
  min-height: 100%;
  padding: clamp(1.5rem, 4vw, 3rem);
  background: radial-gradient(circle at bottom right, rgba(11, 203, 136, 0.16), transparent 45%),
    var(--exercise-bg);
  display: flex;
  align-items: center;
  justify-content: center;
  box-sizing: border-box;
  font-family: 'Noto Sans Sinhala', 'Noto Sans', 'Helvetica Neue', Arial, sans-serif;
}

.match-pairs__surface {
  width: min(760px, 100%);
  background: var(--exercise-surface);
  color: var(--exercise-text);
  border-radius: 24px;
  border: 1px solid rgba(255, 255, 255, 0.12);
  box-shadow: 0 28px 60px rgba(0, 0, 0, 0.32);
  padding: clamp(1.75rem, 3vw, 2.8rem);
  display: flex;
  flex-direction: column;
  gap: clamp(1.25rem, 2.5vw, 2rem);
}

.match-pairs__header {
  display: flex;
  flex-direction: column;
  gap: 0.45rem;
}

.match-pairs__prompt {
  margin: 0;
  font-size: clamp(1.8rem, 4vw, 2.5rem);
  font-weight: 700;
  letter-spacing: 0.015em;
}

.match-pairs__instructions {
  margin: 0;
  font-size: 1rem;
  color: var(--exercise-muted);
}

/* ‚úÖ 2-column grid with row-by-row equal height */
.match-pairs__grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
  margin-top: 1.5rem;
  align-items: stretch;
}

.match-pairs__column {
  display: flex;
  flex-direction: column;
  gap: 0.85rem;
}

.match-pairs__card {
  background: rgba(255, 255, 255, 0.05);
  border: 2px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  padding: 0.85rem 1rem;
  cursor: pointer;
  transition: transform 0.18s ease, border-color 0.18s ease, background 0.18s ease;
  text-align: center;
  font-size: 1.15rem;
  font-weight: 600;
  color: #fff;

  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%; /* ‚úÖ stretches to match the opposite card‚Äôs height */
}

.match-pairs__card:hover,
.match-pairs__card:focus-visible {
  transform: translateY(-2px);
  border-color: rgba(11, 203, 136, 0.8);
  outline: none;
}

.match-pairs__card--selected {
  border-color: var(--exercise-accent-strong);
  background: var(--exercise-accent-soft);
}

.match-pairs__card--matched {
  border-color: var(--exercise-success);
  background: rgba(11, 203, 136, 0.25);
  cursor: default;
}

.match-pairs__card--wrong {
  border-color: rgba(255, 122, 122, 0.9);
  background: rgba(255, 122, 122, 0.18);
}

/* Sinhala + translit */
.match-pairs__card-script {
  display: block;
  font-size: 1.35rem;
  font-weight: 700;
  margin-bottom: 0.25rem;
}

.match-pairs__card-translit {
  display: block;
  font-size: 0.9rem;
  color: rgba(245, 255, 250, 0.65);
}

.match-pairs__feedback {
  margin: 0.5rem 0 0;
  font-weight: 600;
  font-size: 1rem;
  min-height: 1.5em;
  text-align: center;
}

.match-pairs__feedback[data-status='success'] {
  color: var(--exercise-success);
}

.match-pairs__feedback[data-status='error'] {
  color: var(--exercise-danger);
}

.match-pairs__feedback[data-status='neutral'] {
  color: var(--exercise-muted);
}

/* üì± Responsive tweaks */
@media (max-width: 720px) {
  .match-pairs {
    padding: 1.5rem 1.25rem 2.25rem;
  }

  .match-pairs__surface {
    border-radius: 20px;
    padding: 1.75rem 1.25rem 2rem;
  }

  .match-pairs__grid {
    gap: 0.85rem;
  }
}

@media (max-width: 560px) {
  .match-pairs__grid {
    grid-template-columns: 1fr;
  }

  .match-pairs__column {
    flex-direction: row;
    flex-wrap: wrap;
    gap: 0.75rem;
  }

  .match-pairs__card {
    flex: 1 1 calc(50% - 0.75rem);
  }
}

assets/Lessons/exercises/TranslateToTarg.content:
import {
  ensureStylesheet,
  normaliseAnswer,
  normaliseText,
  setStatusMessage,
  createChoiceButton,
  shuffle,
  formatBadge,
} from '../_shared/utils.js';
import { fetchLessonVocab as fetchLessonVocabFromMarkdown } from '../TranslateToBase/index.js';

const DEFAULT_CONTAINER_SELECTOR = '[data-exercise="translate-to-target"]';
const STYLESHEET_ID = 'translate-to-target-styles';

function normaliseVocabEntry(entry) {
  if (!entry || typeof entry !== 'object') {
    return null;
  }

  const si = normaliseText(entry.si) || entry.si;
  const en = normaliseText(entry.en) || entry.en;

  if (!si || !en) {
    return null;
  }

  const transliteration = normaliseText(entry.translit || entry.transliteration);

  return {
    ...entry,
    si,
    en,
    translit: transliteration,
    transliteration: transliteration || entry.transliteration,
  };
}

function pickRandomEntry(entries) {
  if (!Array.isArray(entries) || !entries.length) {
    return null;
  }
  const index = Math.floor(Math.random() * entries.length);
  return entries[index] || null;
}

function shouldShowRomanizedPronunciation() {
  const fallback = true;

  if (typeof window === 'undefined') {
    return fallback;
  }

  const prefs = window.__APP__?.AppState?.prefs;
  if (prefs && typeof prefs.romanized === 'boolean') {
    return prefs.romanized;
  }

  try {
    const stored = window.localStorage.getItem('prefs');
    if (stored) {
      const parsed = JSON.parse(stored);
      if (parsed && typeof parsed.romanized === 'boolean') {
        return parsed.romanized;
      }
    }
  } catch (error) {
    if (typeof console !== 'undefined' && console.warn) {
      console.warn('Unable to read stored pronunciation preference', error);
    }
  }

  return fallback;
}

export function buildTranslateToTargetConfig(vocabEntries) {
  const items = Array.isArray(vocabEntries) ? vocabEntries.map(normaliseVocabEntry).filter(Boolean) : [];

  if (!items.length) {
    throw new Error('TranslateToTarget requires at least one vocab entry.');
  }

  const sinhalaMap = new Map();
  items.forEach((entry) => {
    const label = normaliseText(entry.si);
    if (!label) return;

    const key = label.toLowerCase();
    if (!sinhalaMap.has(key) || entry.priority) {
      sinhalaMap.set(key, entry);
    }
  });

  const uniqueEntries = Array.from(sinhalaMap.values());
  if (uniqueEntries.length < 4) {
    throw new Error('TranslateToTarget requires at least four distinct vocab entries.');
  }

  const selected = pickRandomEntry(uniqueEntries);
  if (!selected) {
    throw new Error('Failed to select a vocab entry for TranslateToTarget.');
  }

  const distractorPool = uniqueEntries.filter((entry) => entry !== selected);
  const distractors = shuffle(distractorPool).slice(0, 3);

  const prompt = normaliseText(selected.en) || selected.en;
  const correctSinhala = normaliseText(selected.si) || selected.si;
  const transliteration = normaliseText(selected.translit || selected.transliteration);
  const baseSuccessMessage = `Correct! '${prompt}' = '${correctSinhala}'.`;

  const choices = shuffle([selected, ...distractors]).map((entry) => {
    const label = normaliseText(entry.si) || entry.si;
    const choiceTransliteration = normaliseText(entry.translit || entry.transliteration);
    return {
      label,
      value: label,
      isCorrect: entry === selected,
      transliteration: choiceTransliteration,
    };
  });

  return {
    badge: 'TRANSLATE',
    prompt,
    transliteration,
    instructions: 'Select the Sinhala translation that matches the English word.',
    successMessage: transliteration
      ? `${baseSuccessMessage} (${transliteration}).`
      : baseSuccessMessage,
    successMessagePlain: baseSuccessMessage,
    errorMessage: `Not quite. '${prompt}' = '${correctSinhala}'. Try again.`,
    choices,
    answers: [correctSinhala],
  };
}

function buildLayout(config, options = {}) {
  const showTransliteration = Boolean(options.showTransliteration);
  const wrapper = document.createElement('section');
  wrapper.className = 'translate-to-target';
  wrapper.dataset.showRomanized = showTransliteration ? 'true' : 'false';

  const surface = document.createElement('div');
  surface.className = 'translate-to-target__surface';
  wrapper.appendChild(surface);

  const header = document.createElement('header');
  header.className = 'translate-to-target__header';
  surface.appendChild(header);

  const badge = document.createElement('span');
  badge.className = 'translate-to-target__badge';
  badge.textContent = formatBadge(config.badge || 'TRANSLATE');
  header.appendChild(badge);

  const prompt = document.createElement('h2');
  prompt.className = 'translate-to-target__prompt';
  prompt.textContent = config.prompt;
  header.appendChild(prompt);

  const choicesContainer = document.createElement('div');
  choicesContainer.className = 'translate-to-target__choices';
  surface.appendChild(choicesContainer);

  const feedback = document.createElement('p');
  feedback.className = 'translate-to-target__feedback';
  feedback.setAttribute('role', 'status');
  feedback.setAttribute('aria-live', 'polite');
  surface.appendChild(feedback);

  const instructions = document.createElement('p');
  instructions.className = 'translate-to-target__instructions';
  instructions.textContent = config.instructions;
  surface.appendChild(instructions);

  return {
    wrapper,
    choicesContainer,
    feedback,
  };
}

function applyChoiceContent(button, choice, showTransliteration) {
  if (!button || !choice) {
    return;
  }

  const label = document.createElement('span');
  label.className = 'translate-to-target__choice-script';
  label.textContent = choice.label;

  button.innerHTML = '';

  if (showTransliteration && choice.transliteration) {
    const transliteration = document.createElement('span');
    transliteration.className = 'translate-to-target__choice-romanized';
    transliteration.textContent = choice.transliteration;
    transliteration.lang = 'si-Latn';
    button.appendChild(transliteration);
  }

  button.appendChild(label);
}

async function fetchLessonVocab() {
  if (typeof window === 'undefined') {
    throw new Error('TranslateToTarget requires a browser environment.');
  }

  const context = window.BashaLanka?.currentLesson || {};
  const detail = context.detail || {};

  const detailVocab = Array.isArray(detail.vocab) ? detail.vocab.map(normaliseVocabEntry).filter(Boolean) : [];
  if (detailVocab.length >= 4) {
    const uniqueSinhala = new Set(detailVocab.map((entry) => normaliseText(entry.si).toLowerCase()).filter(Boolean));
    if (uniqueSinhala.size >= 4) {
      return detailVocab;
    }
  }

  const vocab = await fetchLessonVocabFromMarkdown();
  if (!Array.isArray(vocab) || !vocab.length) {
    throw new Error('Lesson vocab unavailable for TranslateToTarget exercise.');
  }

  return vocab.map(normaliseVocabEntry).filter(Boolean);
}

export async function initTranslateToTargetExercise(options = {}) {
  if (typeof document === 'undefined') {
    throw new Error('TranslateToTarget requires a browser environment.');
  }

  const { target = document.querySelector(DEFAULT_CONTAINER_SELECTOR), onComplete } = options;

  if (!target) {
    throw new Error('TranslateToTarget target element not found.');
  }

  ensureStylesheet(STYLESHEET_ID, './styles.css', { baseUrl: import.meta.url });

  const vocab = await fetchLessonVocab();
  const config = buildTranslateToTargetConfig(vocab);
  const showRomanized = shouldShowRomanizedPronunciation();
  const { wrapper, choicesContainer, feedback } = buildLayout(config, {
    showTransliteration: showRomanized,
  });

  target.innerHTML = '';
  target.appendChild(wrapper);

  const answers = new Set(config.answers.map(normaliseAnswer));
  const state = { completed: false };

  const buttons = config.choices.map((choice) =>
    createChoiceButton({
      label: choice.label,
      value: choice.value ?? choice.label,
      className: 'translate-to-target__choice',
      onClick: (value, button) => {
        if (state.completed) return;
        const normalised = normaliseAnswer(value);
        if (answers.has(normalised)) {
          state.completed = true;
          const successMessage = showRomanized
            ? config.successMessage
            : config.successMessagePlain || config.successMessage;
          setStatusMessage(feedback, successMessage, 'success');
          button.classList.add('translate-to-target__choice--correct');
          buttons.forEach((btn) => {
            btn.disabled = true;
            if (btn !== button) {
              btn.classList.add('translate-to-target__choice--disabled');
            }
          });
          if (typeof onComplete === 'function') {
            onComplete({ value });
          }
        } else {
          button.classList.add('translate-to-target__choice--incorrect');
          setStatusMessage(feedback, config.errorMessage, 'error');
        }
      },
    })
  );

  buttons.forEach((button, index) => {
    const choice = config.choices[index];
    applyChoiceContent(button, choice, showRomanized);
    choicesContainer.appendChild(button);
  });
  setStatusMessage(feedback, '', 'neutral');

  return {
    buttons,
    config,
  };
}

if (typeof window !== 'undefined') {
  window.BashaLanka = window.BashaLanka || {};
  window.BashaLanka.exercises = window.BashaLanka.exercises || {};
  window.BashaLanka.exercises.TranslateToTarget = initTranslateToTargetExercise;
}

export default initTranslateToTargetExercise;

app.practice.js.content:
(function(){
  const container = document.getElementById('view-practice');
  if(!container) return;

  const escapeHtml = (text) => {
    const div = document.createElement('div');
    div.textContent = text;
    return div.innerHTML;
  };

  function getCompletedLessons(){
    try{
      const stored = localStorage.getItem('bashalanka-progress');
      if(!stored) return [];
      const progress = JSON.parse(stored);
      const completed = [];

      Object.keys(progress).forEach(key => {
        const item = progress[key];
        if(item && item.status === 'completed'){
          completed.push({
            id: key,
            title: item.title || `Lesson ${key}`,
            section: item.section || '',
            unit: item.unit || ''
          });
        }
      });

      return completed;
    }catch{
      return [];
    }
  }

  const exerciseTypes = [
    { id: 'match-pairs', label: 'Match Pairs', description: 'Match Sinhala and English' },
    { id: 'translate-to-base', label: 'Translate to English', description: 'Translate from Sinhala' },
    { id: 'translate-to-target', label: 'Translate to Sinhala', description: 'Translate from English' },
    { id: 'listening', label: 'Listening', description: 'Listen and identify' },
    { id: 'picture-choice', label: 'Picture Choice', description: 'Match images with words' },
    { id: 'fill-blank', label: 'Fill in the Blank', description: 'Complete sentences' }
  ];

  function render(){
    const completedLessons = getCompletedLessons();
    const hasCompletedLessons = completedLessons.length > 0;

    const exerciseCheckboxes = exerciseTypes.map(ex => `
      <label class="practice-option">
        <input type="checkbox" name="exercise" value="${escapeHtml(ex.id)}" ${ex.id === 'match-pairs' || ex.id === 'translate-to-base' ? 'checked' : ''}>
        <span class="practice-option__label">
          <strong>${escapeHtml(ex.label)}</strong>
          <small>${escapeHtml(ex.description)}</small>
        </span>
      </label>
    `).join('');

    const lessonOptions = hasCompletedLessons
      ? completedLessons.slice(0, 20).map(lesson => `
          <option value="${escapeHtml(lesson.id)}">${escapeHtml(lesson.title)}</option>
        `).join('')
      : '<option value="" disabled>No completed lessons yet</option>';

    container.innerHTML = `
      <h1 id="practice-title" class="section-title">Practice</h1>
      <p class="muted">Create custom practice sessions from completed lessons.</p>

      <div class="practice-config">
        <div class="card">
          <h2 class="card__title">Practice Session Setup</h2>

          <form id="practiceForm" class="practice-form">
            <div class="practice-form__section">
              <label for="practiceMode" class="practice-label">Practice Mode</label>
              <select id="practiceMode" class="select" name="mode">
                <option value="recent">Recent mistakes</option>
                <option value="weakest">Weakest skills</option>
                <option value="random" selected>Random review</option>
                <option value="specific">Specific lesson</option>
              </select>
            </div>

            <div class="practice-form__section" id="lessonSelectSection" hidden>
              <label for="specificLesson" class="practice-label">Choose Lesson</label>
              <select id="specificLesson" class="select" name="lesson">
                ${lessonOptions}
              </select>
            </div>

            <div class="practice-form__section">
              <label class="practice-label">Exercise Types</label>
              <div class="practice-options">
                ${exerciseCheckboxes}
              </div>
            </div>

            <div class="practice-form__section">
              <label for="duration" class="practice-label">Session Length</label>
              <select id="duration" class="select" name="duration">
                <option value="5">5 exercises (Quick)</option>
                <option value="10" selected>10 exercises (Standard)</option>
                <option value="15">15 exercises (Extended)</option>
                <option value="20">20 exercises (Marathon)</option>
              </select>
            </div>

            <button type="submit" class="btn btn--primary btn--lg" id="startPracticeBtn" ${!hasCompletedLessons ? 'disabled' : ''}>
              Start Practice Session
            </button>

            ${!hasCompletedLessons ? '<p class="practice-hint">Complete some lessons first to unlock practice sessions!</p>' : ''}
          </form>
        </div>

        <div class="practice-stats card">
          <h3 class="card__title">Practice Stats</h3>
          <ul class="stats">
            <li><strong>0</strong> practice sessions</li>
            <li><strong>0</strong> exercises completed</li>
            <li><strong>0%</strong> average accuracy</li>
          </ul>
        </div>
      </div>
    `;

    const form = container.querySelector('#practiceForm');
    const modeSelect = container.querySelector('#practiceMode');
    const lessonSection = container.querySelector('#lessonSelectSection');

    if(modeSelect && lessonSection){
      modeSelect.addEventListener('change', (e) => {
        lessonSection.hidden = e.target.value !== 'specific';
      });
    }

    if(form){
      form.addEventListener('submit', (e) => {
        e.preventDefault();
        handleStartPractice(new FormData(form));
      });
    }
  }

  function handleStartPractice(formData){
    const mode = formData.get('mode');
    const duration = parseInt(formData.get('duration')) || 10;
    const selectedExercises = formData.getAll('exercise');

    if(selectedExercises.length === 0){
      alert('Please select at least one exercise type.');
      return;
    }

    if(typeof window.LessonSimulator !== 'undefined' && window.LessonSimulator.open){
      window.LessonSimulator.open({
        lessonTitle: 'Practice Session',
        lessonNumberText: `${duration} exercises`,
        sectionTitle: 'Practice',
        unitTitle: mode,
        lessonDetail: null,
        lessonMeta: { lessonId: 'practice', skillId: mode, levelId: '' },
        selectedExercises: selectedExercises,
        trigger: null
      });
    }
  }

  const isPracticeView = () => {
    const hash = location.hash || '';
    return hash === '#/practice' || hash === '#practice';
  };

  if(isPracticeView()){
    render();
  }

  window.addEventListener('hashchange', () => {
    if(isPracticeView()){
      render();
    }
  });
})();

assets/Lessons/exercises/TranslateToBase.content:
.translate-to-base {
  --exercise-bg: #00534e;
  --exercise-surface: #003d39;
  --exercise-text: #f5fffa;
  --exercise-muted: rgba(245, 255, 250, 0.64);
  --exercise-accent: #0bcb88;
  --exercise-danger: #ff7a7a;
  width: 100%;
  min-height: 100%;
  padding: clamp(1.5rem, 4vw, 3.25rem) clamp(1rem, 4vw, 3rem);
  background: var(--exercise-bg);
  display: flex;
  justify-content: center;
  align-items: center;
  box-sizing: border-box;
  font-family: 'Noto Sans Sinhala', 'Noto Sans', 'Helvetica Neue', Arial, sans-serif;
}

.translate-to-base__surface {
  background: var(--exercise-surface);
  color: var(--exercise-text);
  border-radius: 24px;
  border: 1px solid rgba(255, 255, 255, 0.12);
  box-shadow: 0 28px 60px rgba(0, 0, 0, 0.28);
  width: min(560px, 100%);
  padding: clamp(1.5rem, 3vw, 2.75rem);
  display: flex;
  flex-direction: column;
  gap: clamp(1.25rem, 2.5vw, 2rem);
}

.translate-to-base__header {
  display: flex;
  flex-direction: column;
  align-items: stretch;
  gap: clamp(0.75rem, 2vw, 1.15rem);
}

.translate-to-base__header-main {
  display: flex;
  align-items: center;
  gap: clamp(0.75rem, 2vw, 1.4rem);
  width: 100%;
}

.translate-to-base__badge {
  font-size: 0.78rem;
  font-weight: 700;
  letter-spacing: 0.09em;
  background: rgba(11, 203, 136, 0.18);
  color: var(--exercise-accent);
  border-radius: 999px;
  padding: 0.35rem 0.9rem;
}

.translate-to-base__mascot {
  width: clamp(56px, 10vw, 72px);
  height: clamp(56px, 10vw, 72px);
  object-fit: contain;
  flex-shrink: 0;
}

.translate-to-base__bubble {
  position: relative;
  flex: 1;
  background: rgba(255, 255, 255, 0.08);
  border-radius: 18px;
  padding: clamp(0.85rem, 2.5vw, 1.35rem) clamp(1rem, 3vw, 1.75rem);
  box-shadow: 0 14px 36px rgba(0, 0, 0, 0.18);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: clamp(0.5rem, 2vw, 0.85rem);
}

.translate-to-base__bubble::after {
  content: '';
  position: absolute;
  left: -12px;
  top: 50%;
  transform: translateY(-50%);
  width: 14px;
  height: 18px;
  background: rgba(255, 255, 255, 0.08);
  clip-path: polygon(0 50%, 100% 0, 100% 100%);
  box-shadow: 0 14px 36px rgba(0, 0, 0, 0.18);
}

/* ==== PROMPT ROW ==== */
.translate-to-base__prompt-row {
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center; /* center row horizontally */
  flex-wrap: nowrap;
  gap: 0.75rem;
}

.translate-to-base__sound {
  flex: 0 0 auto;
  display: grid;
  place-items: center;
  width: clamp(2.25rem, 6vw, 2.75rem);
  height: clamp(2.25rem, 6vw, 2.75rem);
  border-radius: 999px;
  border: 2px solid transparent;
  background: rgba(11, 203, 136, 0.18);
  cursor: pointer;
  transition: transform 0.18s ease, border-color 0.18s ease, background 0.18s ease;
}

.translate-to-base__sound:hover,
.translate-to-base__sound:focus-visible {
  transform: translateY(-1px);
  border-color: rgba(11, 203, 136, 0.8);
  outline: none;
  background: rgba(11, 203, 136, 0.28);
}

.translate-to-base__sound:active {
  transform: translateY(1px);
}

.translate-to-base__sound-icon {
  width: clamp(1.1rem, 3.2vw, 1.35rem);
  height: auto;
  filter: brightness(1.2);
}

.translate-to-base__prompt-si {
  flex: 1 1 auto;
  text-align: center;
  font-size: clamp(1.4rem, 4.5vw, 2.25rem);
  font-weight: 700;
  letter-spacing: 0.02em;
  margin: 0;
  line-height: 1.3;
  word-break: break-word;
  overflow-wrap: anywhere;
}

.translate-to-base__prompt-translit {
  display: block;
  margin-top: 0.35rem;
  text-align: center;
  font-size: clamp(0.95rem, 3vw, 1.1rem);
  color: var(--exercise-muted);
}

/* ==== CHOICES ==== */
.translate-to-base__choices {
  display: grid;
  gap: 0.9rem;
  grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
}

.translate-to-base__choice {
  border: 2px solid transparent;
  border-radius: 18px;
  background: rgba(255, 255, 255, 0.08);
  color: inherit;
  font-size: 1.1rem;
  font-weight: 600;
  padding: 0.85rem 1rem;
  text-align: center;
  transition: transform 0.18s ease, border-color 0.18s ease, background 0.18s ease;
  cursor: pointer;
}

.translate-to-base__choice:hover,
.translate-to-base__choice:focus-visible {
  transform: translateY(-2px);
  border-color: rgba(11, 203, 136, 0.8);
  outline: none;
}

.translate-to-base__choice--correct {
  border-color: rgba(147, 255, 210, 0.95);
  background: rgba(11, 203, 136, 0.25);
  color: #ffffff;
}

.translate-to-base__choice--incorrect {
  border-color: rgba(255, 122, 122, 0.9);
  background: rgba(255, 122, 122, 0.18);
}

.translate-to-base__choice--disabled {
  opacity: 0.65;
  cursor: default;
}

.translate-to-base__feedback {
  margin: 0.5rem 0 0;
  font-size: 1rem;
  min-height: 1.5em;
  font-weight: 600;
  text-align: center;
}

.translate-to-base__feedback[data-status='success'] {
  color: rgba(147, 255, 210, 0.95);
}

.translate-to-base__feedback[data-status='error'] {
  color: var(--exercise-danger);
}

.translate-to-base__feedback[data-status='neutral'] {
  color: var(--exercise-muted);
}

.translate-to-base__instructions {
  margin: 0;
  font-size: 0.95rem;
  text-align: center;
  color: var(--exercise-muted);
}

/* ==== MOBILE FIXES ==== */
@media (max-width: 600px) {
  .translate-to-base {
    padding: 1.5rem 1rem 2rem;
  }

  .translate-to-base__surface {
    border-radius: 20px;
    padding: 1.5rem;
    gap: 1.5rem;
  }

  .translate-to-base__choices {
    grid-template-columns: 1fr;
  }

  /* stack sound above Sinhala text on small screens */
  .translate-to-base__prompt-row {
    flex-direction: column;
    gap: 0.5rem;
  }

  .translate-to-base__sound {
    order: -1; /* sound button above text */
  }

  .translate-to-base__prompt-si {
    font-size: clamp(1.2rem, 6vw, 1.8rem);
    line-height: 1.5;
  }
}

assets/Lessons/exercises/MatchPairs/inde.content:
import {
  ensureStylesheet,
  normaliseText,
  shuffle,
  setStatusMessage,
} from '../_shared/utils.js';
import { fetchLessonVocab } from '../TranslateToBase/index.js';

const DEFAULT_CONTAINER_SELECTOR = '[data-exercise="match-pairs"]';
const STYLESHEET_ID = 'match-pairs-styles';

// üõ† Build config from lesson vocab
function buildMatchPairsConfig(vocabEntries) {
  const items = Array.isArray(vocabEntries)
    ? vocabEntries
        .map((entry) => {
          if (!entry || typeof entry !== 'object') return null;
          const base = normaliseText(entry.si) || entry.si;
          const target = normaliseText(entry.en) || entry.en;
          const translit = normaliseText(entry.translit || entry.transliteration);
          if (!base || !target) return null;
          return { base, target, translit };
        })
        .filter(Boolean)
    : [];

  if (items.length < 2) {
    throw new Error('MatchPairs requires at least two vocab entries.');
  }

  // If more than 5 entries ‚Üí take random 5
  // Otherwise ‚Üí just use all available
  const selected = items.length > 5 ? shuffle(items).slice(0, 5) : items;

  return {
    prompt: 'Match the pairs',
    instructions: 'Match the Sinhala words with their English meanings.',
    successMessage: 'Great match!',
    errorMessage: 'Try again.',
    initialMessage: 'Start matching to continue.',
    pairs: selected.map((entry) => ({
      base: entry.base,
      target: entry.target,
      translit: entry.translit,
    })),
  };
}

function buildLayout(config) {
  const wrapper = document.createElement('section');
  wrapper.className = 'match-pairs';

  const surface = document.createElement('div');
  surface.className = 'match-pairs__surface';
  wrapper.appendChild(surface);

  const header = document.createElement('header');
  header.className = 'match-pairs__header';
  surface.appendChild(header);

  const prompt = document.createElement('h2');
  prompt.className = 'match-pairs__prompt';
  prompt.textContent = config.prompt;
  header.appendChild(prompt);

  const instructions = document.createElement('p');
  instructions.className = 'match-pairs__instructions';
  instructions.textContent = config.instructions;
  surface.appendChild(instructions);

  const grid = document.createElement('div');
  grid.className = 'match-pairs__grid';
  surface.appendChild(grid);

  const targetColumn = document.createElement('div');
  targetColumn.className = 'match-pairs__column match-pairs__column--target';
  grid.appendChild(targetColumn);

  const baseColumn = document.createElement('div');
  baseColumn.className = 'match-pairs__column match-pairs__column--base';
  grid.appendChild(baseColumn);

  const feedback = document.createElement('p');
  feedback.className = 'match-pairs__feedback';
  feedback.setAttribute('role', 'status');
  feedback.setAttribute('aria-live', 'polite');
  surface.appendChild(feedback);

  return {
    wrapper,
    targetColumn,
    baseColumn,
    feedback,
  };
}

function createCard(content, matchId, type) {
  const button = document.createElement('button');
  button.type = 'button';
  button.className = 'match-pairs__card';
  button.classList.add(`match-pairs__card--${type}`);
  button.dataset.matchId = String(matchId);
  button.dataset.cardType = type;

  if (type === 'base') {
    const script = document.createElement('span');
    script.className = 'match-pairs__card-script';
    script.textContent = content.label;
    button.appendChild(script);

    if (content.translit) {
      const transliteration = document.createElement('span');
      transliteration.className = 'match-pairs__card-translit';
      transliteration.textContent = content.translit;
      button.appendChild(transliteration);
    }
  } else {
    const label = document.createElement('span');
    label.className = 'match-pairs__card-label';
    label.textContent = content.label;
    button.appendChild(label);
  }

  return button;
}

export async function initMatchPairsExercise(options = {}) {
  if (typeof document === 'undefined') {
    throw new Error('MatchPairs requires a browser environment.');
  }

  const {
    target = document.querySelector(DEFAULT_CONTAINER_SELECTOR),
    config: configOverride,
    onComplete,
  } = options;

  if (!target) {
    throw new Error('MatchPairs target element not found.');
  }

  ensureStylesheet(STYLESHEET_ID, './styles.css', { baseUrl: import.meta.url });

  let rawConfig;
  if (configOverride && typeof configOverride === 'object') {
    rawConfig = configOverride;
  } else {
    const vocab = await fetchLessonVocab();
    rawConfig = buildMatchPairsConfig(vocab);
  }

  const config = rawConfig;
  const { wrapper, targetColumn, baseColumn, feedback } = buildLayout(config);
  target.innerHTML = '';
  target.appendChild(wrapper);

  const targetCards = [];
  const baseCards = [];
  config.pairs.forEach((pair, index) => {
    baseCards.push(createCard({ label: pair.base, translit: pair.translit }, index, 'base'));
    targetCards.push(createCard({ label: pair.target }, index, 'target'));
  });

  shuffle(targetCards).forEach((card) => targetColumn.appendChild(card));
  shuffle(baseCards).forEach((card) => baseColumn.appendChild(card));

  const cards = [...targetCards, ...baseCards];

  let first = null;
  let locked = false;
  let matched = 0;
  const totalMatches = config.pairs.length;

  cards.forEach((card) => {
    card.addEventListener('click', () => {
      if (locked || card.classList.contains('match-pairs__card--matched')) {
        return;
      }

      if (!first) {
        first = card;
        card.classList.add('match-pairs__card--selected');
        return;
      }

      if (card === first) {
        card.classList.remove('match-pairs__card--selected');
        first = null;
        return;
      }

      locked = true;
      const match = card.dataset.matchId === first.dataset.matchId;
      if (match && card.dataset.cardType !== first.dataset.cardType) {
        card.classList.add('match-pairs__card--matched');
        first.classList.add('match-pairs__card--matched');
        card.disabled = true;
        first.disabled = true;
        matched += 1;
        setStatusMessage(feedback, config.successMessage, 'success');
        if (matched === totalMatches && typeof onComplete === 'function') {
          onComplete({});
        }
        window.setTimeout(() => {
          first?.classList.remove('match-pairs__card--selected');
          first = null;
          locked = false;
        }, 350);
      } else {
        card.classList.add('match-pairs__card--wrong');
        setStatusMessage(feedback, config.errorMessage, 'error');
        window.setTimeout(() => {
          card.classList.remove('match-pairs__card--wrong');
          card.classList.remove('match-pairs__card--selected');
          first?.classList.remove('match-pairs__card--selected');
          first = null;
          locked = false;
        }, 650);
      }
    });
  });

  setStatusMessage(feedback, config.initialMessage || '', 'neutral');

  return {
    cards,
    config,
  };
}

if (typeof window !== 'undefined') {
  window.BashaLanka = window.BashaLanka || {};
  window.BashaLanka.exercises = window.BashaLanka.exercises || {};
  window.BashaLanka.exercises.MatchPairs = initMatchPairsExercise;
}

export default initMatchPairsExercise;

assets/Lessons/exercises/Speak/index.js.content:
import {
  ensureStylesheet,
  loadConfig,
  normaliseAnswer,
  normaliseText,
  createAnswerLookup,
  setStatusMessage,
  supportsSpeechRecognition,
  createSpeechRecognizer,
} from '../_shared/utils.js';

const DEFAULT_CONTAINER_SELECTOR = '[data-exercise="speak"]';
const STYLESHEET_ID = 'speak-styles';

function buildLayout(config) {
  const wrapper = document.createElement('section');
  wrapper.className = 'speak';

  const surface = document.createElement('div');
  surface.className = 'speak__surface';
  wrapper.appendChild(surface);

  const header = document.createElement('header');
  header.className = 'speak__header';
  surface.appendChild(header);

  const prompt = document.createElement('h2');
  prompt.className = 'speak__prompt';
  prompt.textContent = config.prompt;
  header.appendChild(prompt);

  if (config.transliteration) {
    const transliteration = document.createElement('p');
    transliteration.className = 'speak__transliteration';
    transliteration.textContent = config.transliteration;
    surface.appendChild(transliteration);
  }

  const instructions = document.createElement('p');
  instructions.className = 'speak__instructions';
  instructions.textContent = config.instructions;
  surface.appendChild(instructions);

  const button = document.createElement('button');
  button.type = 'button';
  button.className = 'speak__button';
  button.textContent = config.startLabel || 'Start speaking';
  surface.appendChild(button);

  const feedback = document.createElement('p');
  feedback.className = 'speak__feedback';
  feedback.setAttribute('role', 'status');
  feedback.setAttribute('aria-live', 'polite');
  surface.appendChild(feedback);

  const transcript = document.createElement('p');
  transcript.className = 'speak__transcript';
  transcript.textContent = '';
  surface.appendChild(transcript);

  return {
    wrapper,
    button,
    feedback,
    transcript,
  };
}

function prepareConfig(rawConfig) {
  if (!rawConfig || typeof rawConfig !== 'object') {
    throw new Error('Speak config must be an object.');
  }

  const prompt = normaliseText(rawConfig.prompt);
  if (!prompt) {
    throw new Error('Speak config requires a prompt.');
  }

  const instructions = normaliseText(rawConfig.instructions) || 'Tap start and say the phrase aloud.';
  const transliteration = normaliseText(rawConfig.transliteration);
  const answersLookup = createAnswerLookup(rawConfig.answers);

  if (!answersLookup.size) {
    throw new Error('Speak config requires at least one correct answer.');
  }

  return {
    ...rawConfig,
    prompt,
    instructions,
    transliteration,
    answers: Array.from(answersLookup.values()),
    lang: normaliseText(rawConfig.lang) || 'si-LK',
    startLabel: normaliseText(rawConfig.startLabel) || 'Start speaking',
    retryLabel: normaliseText(rawConfig.retryLabel) || 'Try again',
    listeningMessage: normaliseText(rawConfig.listeningMessage) || 'Listening‚Ä¶',
    successMessage: normaliseText(rawConfig.successMessage) || 'Great pronunciation!',
    errorMessage: normaliseText(rawConfig.errorMessage) || 'Try again.',
    initialMessage: normaliseText(rawConfig.initialMessage),
    unsupportedLabel: normaliseText(rawConfig.unsupportedLabel) || 'Speech recognition not supported in this browser',
    unsupportedMessage:
      normaliseText(rawConfig.unsupportedMessage) || 'Try this exercise on a device with microphone access.',
  };
}

export async function initSpeakExercise(options = {}) {
  if (typeof document === 'undefined') {
    throw new Error('Speak exercises require a browser environment.');
  }

  const {
    target = document.querySelector(DEFAULT_CONTAINER_SELECTOR),
    config: configOverride,
    onComplete,
  } = options;

  if (!target) {
    throw new Error('Speak target element not found.');
  }

  ensureStylesheet(STYLESHEET_ID, './styles.css', { baseUrl: import.meta.url });
  const rawConfig = await loadConfig({ config: configOverride, baseUrl: import.meta.url });
  const config = prepareConfig(rawConfig);
  const { wrapper, button, feedback, transcript } = buildLayout(config);
  target.innerHTML = '';
  target.appendChild(wrapper);

  const supported = supportsSpeechRecognition();
  if (!supported) {
    button.disabled = true;
    button.textContent = config.unsupportedLabel;
    setStatusMessage(feedback, config.unsupportedMessage, 'error');
    return { supported: false };
  }

  const answers = config.answers.map(normaliseAnswer);
  const recognizer = createSpeechRecognizer({ lang: config.lang || 'si-LK', maxAlternatives: 5 });
  let listening = false;
  let completed = false;

  recognizer.addEventListener('result', (event) => {
    if (!event.results) return;
    const transcripts = [];
    for (let i = 0; i < event.results.length; i += 1) {
      const result = event.results[i];
      for (let j = 0; j < result.length; j += 1) {
        transcripts.push(result[j].transcript);
      }
    }

    const combined = transcripts.join(' / ');
    transcript.textContent = combined;
    const normalised = transcripts.map(normaliseAnswer);
    if (normalised.some((value) => answers.includes(value))) {
      completed = true;
      setStatusMessage(feedback, config.successMessage, 'success');
      button.disabled = true;
      recognizer.stop();
      if (typeof onComplete === 'function') {
        onComplete({ transcripts });
      }
    } else {
      setStatusMessage(feedback, config.errorMessage, 'error');
    }
  });

  recognizer.addEventListener('end', () => {
    listening = false;
    if (!completed) {
      button.disabled = false;
    button.textContent = config.retryLabel;
    }
  });

  recognizer.addEventListener('error', (event) => {
    listening = false;
    button.disabled = false;
    setStatusMessage(feedback, `${config.errorMessage || 'Something went wrong.'} (${event.error})`, 'error');
  });

  button.addEventListener('click', () => {
    if (listening || completed) return;
    transcript.textContent = '';
    setStatusMessage(feedback, config.listeningMessage || 'Listening‚Ä¶', 'neutral');
    button.disabled = true;
    listening = true;
    try {
      recognizer.start();
    } catch (error) {
      listening = false;
      button.disabled = false;
      setStatusMessage(feedback, config.errorMessage || 'Unable to start recording.', 'error');
    }
  });

  setStatusMessage(feedback, config.initialMessage || '', 'neutral');

  return {
    recognizer,
    config,
  };
}

if (typeof window !== 'undefined') {
  window.BashaLanka = window.BashaLanka || {};
  window.BashaLanka.exercises = window.BashaLanka.exercises || {};
  window.BashaLanka.exercises.Speak = initSpeakExercise;
}

export default initSpeakExercise;

