assets/Lessons/exercises/Listening/style.content:
.listening {
  --exercise-bg: #184b35;
  --exercise-surface: #0f3624;
  --exercise-text: #f5fffa;
  --exercise-muted: rgba(245, 255, 250, 0.7);
  --exercise-accent: #1dd1a1;
  --exercise-danger: #ff7a7a;
  width: 100%;
  min-height: 100%;
  padding: clamp(1.5rem, 4vw, 3rem);
  background: radial-gradient(circle at top left, rgba(29, 209, 161, 0.2), transparent 45%),
    var(--exercise-bg);
  display: flex;
  align-items: center;
  justify-content: center;
  box-sizing: border-box;
  font-family: 'Noto Sans Sinhala', 'Noto Sans', 'Helvetica Neue', Arial, sans-serif;
}

.listening__surface {
  width: min(560px, 100%);
  background: var(--exercise-surface);
  color: var(--exercise-text);
  border-radius: 24px;
  border: 1px solid rgba(255, 255, 255, 0.12);
  box-shadow: 0 28px 60px rgba(0, 0, 0, 0.32);
  padding: clamp(1.75rem, 3vw, 2.75rem);
  display: flex;
  flex-direction: column;
  gap: clamp(1.25rem, 2.5vw, 2rem);
}

.listening__header {
  display: flex;
  flex-direction: column;
  gap: 0.4rem;
}

.listening__prompt {
  margin: 0;
  font-size: clamp(1.8rem, 4vw, 2.5rem);
  font-weight: 700;
  letter-spacing: 0.015em;
}

.listening__play {
  align-self: flex-start;
  border: none;
  border-radius: 16px;
  padding: 0.75rem 1.5rem;
  background: var(--exercise-accent);
  color: #02211a;
  font-weight: 700;
  font-size: 1rem;
  cursor: pointer;
  transition: transform 0.18s ease, box-shadow 0.18s ease;
}

.listening__play:hover,
.listening__play:focus-visible {
  transform: translateY(-1px);
  box-shadow: 0 12px 18px rgba(29, 209, 161, 0.25);
  outline: none;
}

.listening__answer {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.listening__choice {
  border-radius: 18px;
  border: 2px solid transparent;
  background: rgba(255, 255, 255, 0.08);
  color: inherit;
  font-weight: 600;
  font-size: 1.05rem;
  padding: 0.85rem 1rem;
  text-align: center;
  cursor: pointer;
  transition: transform 0.18s ease, border-color 0.18s ease, background 0.18s ease;
}

.listening__choice:hover,
.listening__choice:focus-visible {
  transform: translateY(-2px);
  border-color: rgba(29, 209, 161, 0.8);
  outline: none;
}

.listening__choice--correct {
  border-color: rgba(147, 255, 210, 0.95);
  background: rgba(29, 209, 161, 0.25);
  color: #03241a;
}

.listening__choice--incorrect {
  border-color: rgba(255, 122, 122, 0.9);
  background: rgba(255, 122, 122, 0.18);
}

.listening__choice--disabled {
  opacity: 0.65;
  cursor: default;
}

.listening__input {
  width: 100%;
  border-radius: 16px;
  border: 2px solid transparent;
  background: rgba(255, 255, 255, 0.08);
  color: inherit;
  font-size: 1.05rem;
  padding: 0.85rem 1rem;
  font-family: inherit;
  transition: border-color 0.18s ease, background 0.18s ease;
}

.listening__input:focus-visible {
  outline: none;
  border-color: rgba(29, 209, 161, 0.8);
  background: rgba(255, 255, 255, 0.12);
}

.listening__submit {
  align-self: flex-end;
  border: none;
  border-radius: 16px;
  padding: 0.75rem 1.5rem;
  background: rgba(255, 255, 255, 0.12);
  color: var(--exercise-text);
  font-weight: 700;
  cursor: pointer;
  transition: transform 0.18s ease, background 0.18s ease;
}

.listening__submit:hover,
.listening__submit:focus-visible {
  transform: translateY(-1px);
  background: rgba(255, 255, 255, 0.2);
  outline: none;
}

.listening__feedback {
  margin: 0;
  font-weight: 600;
  font-size: 1rem;
  min-height: 1.5em;
  text-align: center;
}

.listening__feedback[data-status='success'] {
  color: rgba(147, 255, 210, 0.95);
}

.listening__feedback[data-status='error'] {
  color: var(--exercise-danger);
}

.listening__feedback[data-status='neutral'] {
  color: var(--exercise-muted);
}

.listening__instructions {
  margin: 0;
  font-size: 0.95rem;
  color: var(--exercise-muted);
  text-align: center;
}

@media (max-width: 640px) {
  .listening {
    padding: 1.5rem 1.25rem 2.25rem;
  }

  .listening__surface {
    border-radius: 20px;
    padding: 1.5rem;
  }

  .listening__answer {
    gap: 0.65rem;
  }
}

assets/Lessons/exercises/_shared/utils.j.content:
const DEFAULT_FETCH_OPTIONS = {
  credentials: 'same-origin',
  headers: {
    Accept: 'application/json',
  },
};

const ABSOLUTE_URL_PATTERN = /^[a-z][a-z0-9+.-]*:/i;
const PROTOCOL_RELATIVE_PATTERN = /^\/\//;

function normaliseRepoNameSegment(repoName) {
  if (!repoName) {
    return '';
  }

  return String(repoName)
    .trim()
    .replace(/^\/+/, '')
    .replace(/\/+$/, '');
}

export function resolveRepoBasePath(options = {}) {
  const { repoName = 'BashaLanka' } = options;

  if (typeof window === 'undefined' || !window.location) {
    return './';
  }

  const { hostname = '', protocol = '' } = window.location;
  const lowerHost = hostname.toLowerCase();

  if (lowerHost.includes('github.io')) {
    const segment = normaliseRepoNameSegment(repoName);
    return segment ? `/${segment}/` : '/';
  }

  if (
    protocol === 'file:' ||
    lowerHost === 'localhost' ||
    lowerHost === '127.0.0.1' ||
    lowerHost === '::1' ||
    lowerHost === '[::1]'
  ) {
    return './';
  }

  return '/';
}

export function resolveLessonAssetPath(path, options = {}) {
  const { repoName = 'BashaLanka' } = options;

  if (typeof path !== 'string') {
    return path;
  }

  const trimmed = path.trim();

  if (!trimmed) {
    return trimmed;
  }

  if (ABSOLUTE_URL_PATTERN.test(trimmed) || PROTOCOL_RELATIVE_PATTERN.test(trimmed)) {
    return trimmed;
  }

  const cleaned = trimmed.replace(/^\.?\/+/, '');
  const repoBasePath = resolveRepoBasePath({ repoName });

  if (repoBasePath === './') {
    return `./${cleaned}`;
  }

  const baseWithSlash = repoBasePath.endsWith('/') ? repoBasePath : `${repoBasePath}/`;
  return `${baseWithSlash}${cleaned}`;
}

export function normaliseText(value) {
  if (value === null || value === undefined) {
    return '';
  }

  return value
    .toString()
    .trim()
    .replace(/\s+/g, ' ');
}

function resolveStylesheetHref(href, baseUrl) {
  if (!href) {
    return null;
  }

  if (href instanceof URL) {
    return href.toString();
  }

  if (typeof href !== 'string') {
    return null;
  }

  const trimmed = href.trim();

  if (!trimmed) {
    return null;
  }

  if (ABSOLUTE_URL_PATTERN.test(trimmed) || PROTOCOL_RELATIVE_PATTERN.test(trimmed)) {
    return trimmed;
  }

  const candidates = [];

  if (baseUrl) {
    candidates.push(baseUrl);
  }

  if (typeof document !== 'undefined' && document.baseURI) {
    candidates.push(document.baseURI);
  }

  candidates.push(import.meta.url);

  for (const base of candidates) {
    try {
      return new URL(trimmed, base).toString();
    } catch (error) {
      // Continue to the next candidate.
    }
  }

  return null;
}

export function ensureStylesheet(id, relativeHref, options = {}) {
  if (typeof document === 'undefined') return;
  if (!id || !relativeHref) return;

  if (document.getElementById(id)) {
    return;
  }

  const baseUrl =
    typeof options === 'string'
      ? options
      : options && typeof options === 'object'
      ? options.baseUrl
      : null;

  const resolvedHref = resolveStylesheetHref(relativeHref, baseUrl);

  if (!resolvedHref) {
    console.error('Unable to resolve stylesheet URL', { id, relativeHref, baseUrl });
    return;
  }

  const link = document.createElement('link');
  link.id = id;
  link.rel = 'stylesheet';
  link.href = resolvedHref;

  document.head.appendChild(link);
}

export function normaliseAnswer(value) {
  return normaliseText(value).toLowerCase();
}

export function shuffle(array) {
  const items = Array.isArray(array) ? array.slice() : [];
  for (let i = items.length - 1; i > 0; i -= 1) {
    const j = Math.floor(Math.random() * (i + 1));
    [items[i], items[j]] = [items[j], items[i]];
  }
  return items;
}

export function createAudio(src) {
  if (typeof Audio === 'undefined' || !src) {
    return null;
  }

  const audio = new Audio();
  audio.src = src;
  return audio;
}

function resolveConfigUrl(path, { isCustomPath, baseUrl } = {}) {
  if (!path || typeof path !== 'string') {
    return null;
  }

  const trimmed = path.trim();

  if (/^https?:\/\//i.test(trimmed)) {
    return trimmed;
  }

  const candidateBases = [];

  if (isCustomPath) {
    if (typeof document !== 'undefined' && document.baseURI) {
      candidateBases.push(document.baseURI);
    }

    if (
      typeof window !== 'undefined' &&
      window.location &&
      typeof window.location.href === 'string'
    ) {
      candidateBases.push(window.location.href);
    }
  }

  if (baseUrl) {
    candidateBases.push(baseUrl);
  }

  candidateBases.push(import.meta.url);

  for (const base of candidateBases) {
    try {
      return new URL(trimmed, base).toString();
    } catch (error) {
      // Continue trying the next base candidate.
    }
  }

  return null;
}

export async function loadConfig(options = {}) {
  const { config, fallbackPath = './config.json', baseUrl = null } = options;

  if (config && typeof config === 'object') {
    return config;
  }

  const isCustomPath = typeof config === 'string' && config.trim().length > 0;
  const path = isCustomPath ? config : fallbackPath;

  if (typeof path !== 'string' || !path.trim()) {
    throw new Error('Exercise configuration must be an object or JSON path.');
  }

  if (typeof fetch === 'undefined') {
    throw new Error('Fetching exercise configuration requires a browser environment.');
  }

  const resolvedUrl = resolveConfigUrl(path, { isCustomPath, baseUrl });

  if (!resolvedUrl) {
    throw new Error(`Failed to resolve configuration path: ${path}`);
  }

  const response = await fetch(resolvedUrl, DEFAULT_FETCH_OPTIONS);
  if (!response.ok) {
    throw new Error(`Failed to load configuration file: ${path}`);
  }

  return response.json();
}

export function setStatusMessage(element, message, variant = 'neutral') {
  if (!element) return;
  element.textContent = message || '';
  element.setAttribute('data-status', variant);
}

export function announce(element, message) {
  if (!element || typeof document === 'undefined') return;
  const liveRegion = document.createElement('div');
  liveRegion.setAttribute('role', 'status');
  liveRegion.setAttribute('aria-live', 'polite');
  liveRegion.className = 'sr-only';
  liveRegion.textContent = message;
  document.body.appendChild(liveRegion);
  window.setTimeout(() => {
    document.body.removeChild(liveRegion);
  }, 750);
}

export function toggleBusy(element, busy) {
  if (!element) return;
  if (busy) {
    element.setAttribute('aria-busy', 'true');
  } else {
    element.removeAttribute('aria-busy');
  }
}

export function formatBadge(text) {
  if (!text) return '';
  return String(text).toUpperCase();
}

export function createAnswerLookup(initialAnswers = []) {
  const lookup = new Map();
  if (Array.isArray(initialAnswers)) {
    initialAnswers.forEach((value) => {
      addAnswerToLookup(lookup, value);
    });
  }
  return lookup;
}

export function addAnswerToLookup(lookup, value) {
  if (!(lookup instanceof Map)) return;
  const text = normaliseText(value);
  if (!text) return;
  const key = normaliseAnswer(text);
  if (!key || lookup.has(key)) return;
  lookup.set(key, text);
}

export function answerLookupHas(lookup, value) {
  if (!(lookup instanceof Map)) return false;
  const key = normaliseAnswer(normaliseText(value));
  return lookup.has(key);
}

export function normaliseChoiceItem(choice, options = {}) {
  const {
    labelKey = 'label',
    valueKey = 'value',
    fallbackLabelKeys = [],
    fallbackValueKeys = [],
    allowString = true,
  } = options;

  if (choice === null || choice === undefined) {
    return null;
  }

  if (allowString && (typeof choice === 'string' || typeof choice === 'number')) {
    const label = normaliseText(choice);
    if (!label) return null;
    return {
      label,
      value: label,
      isCorrect: false,
    };
  }

  if (typeof choice !== 'object') {
    return null;
  }

  const next = { ...choice };

  const labelCandidates = [next[labelKey], next[valueKey], ...fallbackLabelKeys.map((key) => next[key])];
  let label = '';
  for (const candidate of labelCandidates) {
    const clean = normaliseText(candidate);
    if (clean) {
      label = clean;
      break;
    }
  }

  if (!label) {
    return null;
  }

  const valueCandidates = [next[valueKey], next[labelKey], ...fallbackValueKeys.map((key) => next[key])];
  let value = '';
  for (const candidate of valueCandidates) {
    const clean = normaliseText(candidate);
    if (clean) {
      value = clean;
      break;
    }
  }

  if (!value) {
    value = label;
  }

  let { isCorrect } = next;
  if (isCorrect === undefined && next.correct !== undefined) {
    isCorrect = Boolean(next.correct);
  }

  return {
    ...next,
    label,
    value,
    isCorrect: Boolean(isCorrect),
  };
}

export function createChoiceButton({ label, value, onClick, className }) {
  const button = document.createElement('button');
  button.type = 'button';
  button.className = className || 'exercise__choice';
  button.textContent = label;
  if (value !== undefined) {
    button.value = value;
  }
  if (typeof onClick === 'function') {
    button.addEventListener('click', () => onClick(value, button));
  }
  return button;
}

export function createTile(text) {
  const button = document.createElement('button');
  button.type = 'button';
  button.className = 'exercise__tile';
  button.textContent = text;
  button.dataset.tileValue = text;
  return button;
}

export function supportsSpeechRecognition() {
  if (typeof window === 'undefined') return false;
  return (
    'SpeechRecognition' in window ||
    'webkitSpeechRecognition' in window ||
    'mozSpeechRecognition' in window ||
    'msSpeechRecognition' in window
  );
}

export function createSpeechRecognizer(options = {}) {
  if (!supportsSpeechRecognition()) {
    return null;
  }

  const Constructor =
    window.SpeechRecognition ||
    window.webkitSpeechRecognition ||
    window.mozSpeechRecognition ||
    window.msSpeechRecognition;

  const recognizer = new Constructor();
  recognizer.lang = options.lang || 'si-LK';
  recognizer.interimResults = Boolean(options.interimResults);
  recognizer.maxAlternatives = options.maxAlternatives || 3;
  return recognizer;
}

