.wordbank {
  max-width: 680px;
  margin: 0 auto;
  padding: var(--space-lg, 1.5rem);
  background: var(--color-elev, #121f36);
  border-radius: var(--radius-lg, 1.5rem);
  box-shadow: var(--shadow-2, 0 12px 32px rgba(0, 0, 0, 0.3));
  color: var(--color-text, #f5f8ff);
  font-family: var(--font-sans, system-ui, sans-serif);
  display: flex;
  flex-direction: column;
  gap: var(--space-md, 1rem);
  text-align: center;
}

.wordbank__prompt {
  display: flex;
  justify-content: center;
  text-align: center;
}

.wordbank__prompt-wrapper {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: var(--space-sm, 0.75rem);
}

.wordbank__prompt-bubble {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: var(--space-xs, 0.5rem);
  padding: var(--space-sm, 0.75rem) var(--space-lg, 1.5rem);
  border-radius: var(--radius-lg, 1.25rem);
  background: rgba(32, 37, 64, 0.96);
  border: 1px solid rgba(108, 125, 197, 0.65);
  box-shadow: 0 10px 28px rgba(8, 12, 34, 0.45);
  max-width: min(480px, 100%);
  order: 0;
}

.wordbank__unit-illustration {
  width: clamp(72px, 64px + 6vw, 128px);
  height: auto;
  order: 1;
}

.wordbank__prompt-si,
.wordbank__prompt-en {
  font-size: clamp(1.35rem, 1.1rem + 0.9vw, 1.85rem);
  font-weight: 600;
  letter-spacing: 0.01em;
}

.wordbank__prompt-en {
  font-family: var(--font-sans, system-ui, sans-serif);
}

.wordbank__prompt-translit {
  font-size: clamp(0.9rem, 0.85rem + 0.25vw, 1rem);
  color: var(--color-muted, #a9b6c6);
}

.wordbank__instruction {
  margin: 0 auto;
  max-width: 500px;
  font-size: clamp(0.95rem, 0.9rem + 0.2vw, 1.05rem);
  color: var(--color-muted, #a9b6c6);
}

.wordbank__tiles {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: var(--space-sm, 0.75rem);
}

.wordbank__tile {
  padding: 0.55rem 1.1rem;
  border-radius: var(--radius-sm, 0.75rem);
  border: 1px solid rgba(120, 138, 196, 0.4);
  background: var(--color-surface, #101a2b);
  color: var(--color-text, #f5f8ff);
  font-weight: 600;
  letter-spacing: 0.01em;
  transition: transform 0.18s ease, box-shadow 0.18s ease, border-color 0.18s ease, background 0.18s ease;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.2rem;
}

.wordbank__tile:not(:disabled):hover,
.wordbank__tile:not(:disabled):focus-visible {
  transform: translateY(-2px);
  border-color: var(--color-accent, #3ec0ff);
  box-shadow: var(--shadow-1, 0 1px 2px rgba(0, 0, 0, 0.2), 0 8px 24px rgba(0, 0, 0, 0.25));
}

.wordbank__tile:disabled {
  opacity: 0.45;
  cursor: default;
  transform: none;
}

.wordbank__answer-label {
  font-size: clamp(0.9rem, 0.85rem + 0.2vw, 1rem);
  color: var(--color-muted, #a9b6c6);
}

.wordbank__answer {
  min-height: 3.25rem;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: var(--space-sm, 0.75rem);
  padding: var(--space-sm, 0.75rem);
  border-radius: var(--radius-md, 1rem);
  border: 1px dashed rgba(108, 125, 197, 0.45);
  background: rgba(22, 29, 52, 0.7);
}

.wordbank__answer-placeholder {
  color: var(--color-muted, #a9b6c6);
  font-style: italic;
}

.wordbank__answer-tile {
  padding: 0.55rem 1.05rem;
  border-radius: var(--radius-sm, 0.75rem);
  border: none;
  background: var(--color-accent, #3ec0ff);
  color: #04152c;
  font-weight: 700;
  letter-spacing: 0.01em;
  box-shadow: var(--shadow-1, 0 1px 2px rgba(0, 0, 0, 0.2), 0 8px 24px rgba(0, 0, 0, 0.25));
  transition: transform 0.18s ease, background 0.18s ease, box-shadow 0.18s ease;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.2rem;
}

.wordbank__answer-tile:hover,
.wordbank__answer-tile:focus-visible {
  transform: translateY(-1px);
  background: var(--color-accent-600, #1aa8ef);
  color: #f5f8ff;
}

.wordbank__tile-text {
  font-size: clamp(1.1rem, 1rem + 0.5vw, 1.35rem);
  font-weight: inherit;
}

.wordbank__tile-translit {
  font-size: clamp(0.75rem, 0.7rem + 0.2vw, 0.9rem);
  color: var(--color-muted, #a9b6c6);
  font-weight: 500;
}

.wordbank__answer-tile .wordbank__tile-text {
  color: inherit;
}

.wordbank__answer-tile .wordbank__tile-translit {
  color: rgba(4, 21, 44, 0.8);
}

.wordbank__feedback {
  min-height: 1.5rem;
  font-size: clamp(0.95rem, 0.9rem + 0.2vw, 1.05rem);
  color: var(--color-muted, #a9b6c6);
}

.wordbank__feedback[data-state="correct"] {
  color: var(--color-positive, #68d391);
}

.wordbank__feedback[data-state="incorrect"] {
  color: var(--color-danger, #f87171);
}

.wordbank__actions {
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: var(--space-sm, 0.75rem);
}

.wordbank__actions button {
  min-width: 120px;
  padding: 0.65rem 1.75rem;
  border-radius: 999px;
  border: 1px solid transparent;
  background: var(--color-surface, #101a2b);
  color: var(--color-text, #f5f8ff);
  font-weight: 600;
  letter-spacing: 0.01em;
  transition: transform 0.18s ease, box-shadow 0.18s ease, background 0.18s ease, border-color 0.18s ease;
}

.wordbank__actions button[data-variant="primary"] {
  background: var(--color-accent, #3ec0ff);
  color: #03162a;
}

.wordbank__actions button[data-variant="primary"]:hover,
.wordbank__actions button[data-variant="primary"]:focus-visible {
  background: var(--color-accent-600, #1aa8ef);
  color: #f5f8ff;
}

.wordbank__actions button[data-variant="secondary"] {
  border-color: rgba(108, 125, 197, 0.45);
}

.wordbank__actions button[data-variant="secondary"]:hover,
.wordbank__actions button[data-variant="secondary"]:focus-visible {
  border-color: var(--color-accent, #3ec0ff);
  box-shadow: var(--shadow-1, 0 1px 2px rgba(0, 0, 0, 0.2), 0 8px 24px rgba(0, 0, 0, 0.25));
}

.wordbank__actions button[data-variant="ghost"] {
  background: transparent;
  border-color: rgba(108, 125, 197, 0.2);
  color: var(--color-muted, #a9b6c6);
}

.wordbank__actions button[data-variant="ghost"]:hover,
.wordbank__actions button[data-variant="ghost"]:focus-visible {
  border-color: rgba(108, 125, 197, 0.45);
  color: var(--color-text, #f5f8ff);
}

@media (max-width: 560px) {
  .wordbank {
    padding: var(--space-md, 1rem);
  }

  .wordbank__prompt-bubble {
    padding: var(--space-sm, 0.75rem) var(--space-md, 1rem);
  }

  .wordbank__answer {
    padding: var(--space-xs, 0.5rem);
  }

  .wordbank__answer-tile,
  .wordbank__tile {
    padding-inline: 0.85rem;
  }
}

assets/Lessons/exercises/PictureChoice/i.content:
import {
  ensureStylesheet,
  loadConfig,
  normaliseAnswer,
  normaliseText,
  createAnswerLookup,
  addAnswerToLookup,
  answerLookupHas,
  normaliseChoiceItem,
  setStatusMessage,
} from '../_shared/utils.js';

const DEFAULT_CONTAINER_SELECTOR = '[data-exercise="picture-choice"]';
const STYLESHEET_ID = 'picture-choice-styles';

function buildLayout(config) {
  const wrapper = document.createElement('section');
  wrapper.className = 'picture-choice';

  const surface = document.createElement('div');
  surface.className = 'picture-choice__surface';
  wrapper.appendChild(surface);

  const header = document.createElement('header');
  header.className = 'picture-choice__header';
  surface.appendChild(header);

  const prompt = document.createElement('h2');
  prompt.className = 'picture-choice__prompt';
  prompt.textContent = config.prompt;
  header.appendChild(prompt);

  const choices = document.createElement('div');
  choices.className = 'picture-choice__choices';
  surface.appendChild(choices);
  const feedback = document.createElement('p');
  feedback.className = 'picture-choice__feedback';
  feedback.setAttribute('role', 'status');
  feedback.setAttribute('aria-live', 'polite');
  surface.appendChild(feedback);

  const instructions = document.createElement('p');
  instructions.className = 'picture-choice__instructions';
  instructions.textContent = config.instructions;
  surface.appendChild(instructions);

  return {
    wrapper,
    choices,
    choices,
    feedback,
  };
}

function createPictureButton(option, onClick) {
  const button = document.createElement('button');
  button.type = 'button';
  button.className = 'picture-choice__option';
  button.addEventListener('click', () => onClick(option, button));

  const image = document.createElement('img');
  image.className = 'picture-choice__image';
  image.src = option.image;
  image.alt = option.alt || option.label || '';
  button.appendChild(image);

  const label = document.createElement('span');
  label.className = 'picture-choice__label';
  label.textContent = option.label;
  button.appendChild(label);

  return button;
}

function prepareConfig(rawConfig) {
  if (!rawConfig || typeof rawConfig !== 'object') {
    throw new Error('PictureChoice config must be an object.');
  }

  const prompt = normaliseText(rawConfig.prompt);
  if (!prompt) {
    throw new Error('PictureChoice config requires a prompt.');
  }

  const instructions = normaliseText(rawConfig.instructions) || 'Select the picture that matches the prompt.';

  const answersLookup = createAnswerLookup(rawConfig.answers);
  const rawChoices = Array.isArray(rawConfig.choices) ? rawConfig.choices : [];
  const choices = rawChoices
    .map((choice) =>
      normaliseChoiceItem(choice, {
        fallbackLabelKeys: ['value', 'text'],
        fallbackValueKeys: ['value', 'label'],
        allowString: false,
      })
    )
    .filter((choice) => choice && choice.label && normaliseText(choice.image));

  choices.forEach((choice) => {
    if (choice.isCorrect) {
      addAnswerToLookup(answersLookup, choice.value || choice.label);
    }
  });

  if (!choices.length) {
    throw new Error('PictureChoice config requires at least one option with an image.');
  }

  if (!answersLookup.size) {
    throw new Error('PictureChoice config requires at least one correct answer.');
  }

  const preparedChoices = choices.map((choice) => {
    const value = choice.value || choice.label;
    const isCorrect =
      choice.isCorrect ||
      answerLookupHas(answersLookup, value) ||
      answerLookupHas(answersLookup, choice.label);
    return {
      ...choice,
      label: choice.label,
      value,
      isCorrect,
    };
  });

  return {
    ...rawConfig,
    prompt,
    instructions,
    choices: preparedChoices,
    answers: Array.from(answersLookup.values()),
    successMessage: normaliseText(rawConfig.successMessage) || 'Correct! Nice work.',
    errorMessage: normaliseText(rawConfig.errorMessage) || 'Not quite, try again.',
    initialMessage: normaliseText(rawConfig.initialMessage),
  };
}

export async function initPictureChoiceExercise(options = {}) {
  if (typeof document === 'undefined') {
    throw new Error('PictureChoice requires a browser environment.');
  }

  const {
    target = document.querySelector(DEFAULT_CONTAINER_SELECTOR),
    config: configOverride,
    onComplete,
  } = options;

  if (!target) {
    throw new Error('PictureChoice target element not found.');
  }

  ensureStylesheet(STYLESHEET_ID, './styles.css', { baseUrl: import.meta.url });
  const rawConfig = await loadConfig({ config: configOverride, baseUrl: import.meta.url });
  const config = prepareConfig(rawConfig);
  const { wrapper, choices, feedback } = buildLayout(config);
  target.innerHTML = '';
  target.appendChild(wrapper);

  const answers = new Set(config.answers.map(normaliseAnswer));
  const buttons = [];
  let completed = false;

  config.choices.forEach((choice) => {
    const button = createPictureButton(choice, (option, element) => {
      if (completed) return;
      const normalised = normaliseAnswer(option.value || option.label);
      if (answers.has(normalised)) {
        completed = true;
        element.classList.add('picture-choice__option--correct');
        buttons.forEach((btn) => {
          btn.disabled = true;
          if (btn !== element) {
            btn.classList.add('picture-choice__option--disabled');
          }
        });
        setStatusMessage(feedback, config.successMessage, 'success');
        if (typeof onComplete === 'function') {
          onComplete({ value: option });
        }
      } else {
        element.classList.add('picture-choice__option--incorrect');
        setStatusMessage(feedback, config.errorMessage, 'error');
      }
    });

    choices.appendChild(button);
    buttons.push(button);
  });

  setStatusMessage(feedback, config.initialMessage || '', 'neutral');

  return {
    buttons,
    config,
  };
}

if (typeof window !== 'undefined') {
  window.BashaLanka = window.BashaLanka || {};
  window.BashaLanka.exercises = window.BashaLanka.exercises || {};
  window.BashaLanka.exercises.PictureChoice = initPictureChoiceExercise;
}

export default initPictureChoiceExercise;
